import React, { useEffect, useMemo, useState, useRef } from 'react';
import {
  Page,
  Navbar,
  PageContent,
  Popup,
  Link,
  NavRight,
  Searchbar,
  NavLeft,
  NavTitle,
  Toggle,
  Card,
  CardContent,
  Input,
  Toolbar,
  Tabs,
  Tab,
  Button
} from '@hvisions/f-ui';
import { Skeleton, Empty } from '~/components';
import { isEmpty } from 'lodash';
import 'react-circular-progressbar/dist/styles.css';
import backIcon from '../../../../img/backIcon.png';
import styles from './style.scss';
import { goBack, onToast, createDialog } from '~/util/home';
import CardInfo from './CardInfo';
import useDebounce from '~/Hook/useDebounce';

const Master1 = ({ $f7, f7router }) => {
  const countRef = useRef(10);
  const [loading, setLoading] = useState(false);
  const [list, setList] = useState([]);
  const [total, setTotal] = useState(0);
  const [allowInfinite, setAllowInfinite] = useState(true);
  const [showPreloader, setShowPreloader] = useState(false);
  const [ptrPreloader, setPtrPreloader] = useState(false);
  const [selectValue, setSelectValue] = useState('');
  const debounceSelectValue = useDebounce(selectValue, 500);
  const [tabKey, setTabKey] = useState(0);
  const [putInStoragestate, setPutInStoragestate] = useState({
    0: '出库中',
    1: '已完成',
    2: '全部'
  });

  const renderCardList = () =>
    !loading ? (
      !isEmpty(list) ? (
        list.map(value => (
          <CardInfo
            key={value.id}
            item={value}
            f7router={f7router}
          />
        ))
      ) : (
        <Empty />
      )
    ) : (
      <Skeleton />
    );


  const handleDetail = () => {
    f7router.navigate('/yw-retrieval-operate', { props: { state: 0 } });
  };
  return (
    <Page
      infinite
      infiniteDistance={50}
      infinitePreloader={showPreloader}
      ptrPreloader={ptrPreloader}
      ptr
      onPtrPullStart={() => {
        setPtrPreloader(true);
      }}
      className={styles.pageContainer}
    >
      <Navbar>
        <NavLeft>
          <a onClick={() => goBack('/wareManage')} className={styles['nav-left']}>
            <img alt="" style={{ height: 24 }} src={backIcon} />
          </a>
        </NavLeft>
        <NavTitle>成品出库</NavTitle>
        <NavRight className={styles['nav-right']}>
          <Link
            searchbarEnable=".searchbar-demo"
            iconIos="f7:search"
            iconAurora="f7:search"
            iconMd="material:search"
          ></Link>
        </NavRight>
      </Navbar>
      <div className={styles.tabContainer}>{renderCardList()}</div>
      <div className={styles['detail-bottom']}>
        <Button className={styles['bottom-btn-confirm']} fill round >
          新增
        </Button>
      </div>
    </Page>
  );
};

export default Master1;
