import React, { useEffect, useMemo, useRef, useState } from 'react';
import { Toolbar, Link, Card, Tab, Block, SkeletonBlock, Page, Navbar, NavLeft, NavTitle, NavRight, List, Subnavbar, Searchbar, ListItem, PageContent, Button, Sheet, Popup, Input } from '@hvisions/f-ui';
import styles from './style.scss';
import backIcon from '~/pages/WarehousinManage/img/backIcon.png';
import { i18n, page } from '@hvisions/toolkit';
import { onToast, createDialog } from '~/util/home';
import useDebounce from '~/Hook/useDebounce';
import RawMaterialWarehousingApi from '~/api/RawMaterialWarehousing';
import EmptyPalletDeliveryApi from '~/api/EmptyPalletDelivery';
import TransferBoxServices from '~/api/TransferBox';

const Manual = ({ f7router, moduleList, ...props }) => {
  const [tabKey, setTabKey] = useState(0);
  const [state, setState] = useState({
    0: '新建', 1: '称重中', 2: '已完成'
  });
  const countRef = useRef(10);
  const [loading, setLoading] = useState(false);
  const [list, setList] = useState([]);
  const [total, setTotal] = useState(0);
  const [selectValue, setSelectValue] = useState('');
  const debounceSelectValue = useDebounce(selectValue, 500);
  const [showPreloader, setShowPreloader] = useState(false);
  const [allowInfinite, setAllowInfinite] = useState(true);
  const [ptrPreloader, setPtrPreloader] = useState(false);

  const newStyle = {
    background: '#ffdad4',
    color: '#d83333'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };

  useEffect(() => {
    loadData();
}, []);

  const loadData = async keyWord => {
    setLoading(true);
    const searchData = {
      pageSize: countRef.current,
      type: 0 ,
    };
    console.log('searchData',searchData);
    await TransferBoxServices.getPage(searchData)
    .then(res => {
      console.log('res',res);
        setList(res.content);
        setTotal(res.totalElements);
    })
      .catch(err => {
        onToast(err.message, styles.toastError);
      });
    setLoading(false);
  };

  const loadMore = async () => {
    if (!allowInfinite) return;
    setShowPreloader(true);
    if (countRef.current >= total) {
      setShowPreloader(false);
      return;
    }
    setAllowInfinite(false);
    countRef.current = countRef.current + 10;
    await loadData(selectValue);
    await setAllowInfinite(true);
  };

  const onHandleRefresh = async done => {
    await setPtrPreloader(true);
    await loadData(selectValue);
    await setPtrPreloader(false);
    await done();
  };

  const handleAutomatic = ()=>{
    createDialog(
      '托盘自动下架',
      '确认开始托盘自动下架流程？',
      function() {
        try {
          Automatic()
          // onToast('托盘自动下架成功', styles.toastSuccess);
        } catch (error) {
          console.log('error',error);
          onToast('托盘自动下架失败', styles.toastError);
        }
      }
    );
  }

  const Automatic = async () => {
    const data ={
      destination:'原材料组托点',
      middle:'J001',
      taskType:6, //原料托盘出库
      transferType:0 //原料托盘
    }
    await EmptyPalletDeliveryApi.autoTransferOut(data)
    .then(res=>{
      onToast('托盘自动下架成功', styles.toastSuccess);
      loadData(selectValue);
    })
    .catch(err=>{
      onToast(err.message, styles.toastError);
    })
    //托盘出库   托盘自动出库
  }

  return (
    <Page pageContent={false}>
      <Navbar>
        <NavLeft>
          <a onClick={() => f7router.back()} className="ne-navleft">
            <img alt="" style={{ height: 24 }} src={backIcon} />
          </a>
        </NavLeft>
        <NavTitle>托盘手动下架</NavTitle>
      </Navbar>
      <PageContent
        infinite
        infiniteDistance={50}
        infinitePreloader={showPreloader}
        onInfinite={loadMore}
        ptrPreloader={ptrPreloader}
        ptr
        onPtrRefresh={onHandleRefresh}
        onPtrPullStart={() => {
          setPtrPreloader(true);
        }}
      >
        <div style={{ padding: '0 16px' }} className={styles.tabContainer}>
        <ul className={styles['div-ul']}>
          <li>
            <span className={styles['li-title']}>{'托盘手动下架'}</span>
          </li>
          {list.map((value, index) => {
            return (
              <Card key={index} className={styles['card']}>
                <ul className={styles['card-ul']}>
                  <li>
                    <span className={styles['li-next-title']}>托盘号</span>
                    <span className={styles['li-next-title']}>{value.code || ''}</span>
                  </li>
                  <li>
                    <span className={styles['li-next-title']}>任务状态</span>
                    <span className={styles['li-next-title']}>{value.taskStatus || ''}</span>
                  </li>
                  <li>
                    <span className={styles['li-next-title']}>托盘状态</span>
                    <span
                      className={styles['li-status']}
                      style={value.state == '空闲' ? newStyle : onGoingStyle}
                    >
                      {value.state || ''}
                    </span>
                  </li>
                </ul>
                <div className={styles['card-div']}>
                  <Button
                    fill
                    round
                    className={styles['bottom-btn-confirm']}
                    // onClick={handleConfirm}
                  >
                    下架
                  </Button>
                </div>
              </Card>
            );
          })}
        </ul>
        </div>
      </PageContent>
    </Page>
  );
};

export default Manual;
