import React, { useEffect, useState } from 'react';
import { Card, Button } from '@hvisions/f-ui';
import styles from './cardInfo.scss';
import { Sheet } from 'framework7-react';
import CardSheet from './CardSheet/index';

const CardInfo = ({
  item,
}) => {
  const [style, setStyle] = useState();
  const [state, setState] = useState();
  const [sheetValue, setSheetValue] = useState({});
  const [sheetOpened, setSheetOpened] = useState(false);

  useEffect(() => {
    if (item.state == 0) { setStyle(newStyle); setState('新建') }
    if (item.state == 1) { setStyle(onGoingStyle); setState('执行中') }
    if (item.state == 2) { setStyle(completeStyle); setState('已完成') }
  }, []);

  const newStyle = {
    background: '#ffdad4',
    color: '#d83333'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };
  const completeStyle = {
    background: '#D3F0E9',
    color: '#42BB9E'
  };

  const columns = [
    // {
    //   title: '收料单号',
    //   dataIndex: 'receiptNumber',
    //   key: 'receiptNumber',
    //   align: 'center',
    // },
    // {
    //   title: '行号',
    //   dataIndex: 'lineNumber',
    //   key: 'lineNumber',
    //   align: 'center',
    // },

    {
      title: '关联单号',
      dataIndex: 'associatedNumber',
      key: 'associatedNumber',
      align: 'center',
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      key: 'createTime',
      align: 'center',
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center',
    },
    // {
    //   title: '单据数量',
    //   dataIndex: 'number',
    //   key: 'number',
    //   align: 'center',
    // },
    // {
    //   title: '单据物料编码',
    //   dataIndex: 'materialCode',
    //   key: 'materialCode',
    //   align: 'center',
    // },
    // {
    //   title: '单据物料名称',
    //   dataIndex: 'materialName',
    //   key: 'materialName',
    //   align: 'center',
    // },
    // {
    //   title: '托盘号',
    //   dataIndex: 'trayNumber',
    //   key: 'trayNumber',
    //   align: 'center',
    // },
    // {
    //   title: '规格',
    //   dataIndex: 'specifications',
    //   key: 'specifications',
    //   align: 'center',
    // },
    // {
    //   title: '实际重量',
    //   dataIndex: 'actualWeight',
    //   key: 'actualWeight',
    //   align: 'center',
    // },
    // {
    //   title: '实际数量',
    //   dataIndex: 'actualNumber',
    //   key: 'actualNumber',
    //   align: 'center',
    // },
    // {
    //   title: '实际物料编码',
    //   dataIndex: 'realityMaterialCode',
    //   key: 'realityMaterialCode',
    //   align: 'center',
    // },
    // {
    //   title: '实际物料名称',
    //   dataIndex: 'realityMaterialName',
    //   key: 'realityMaterialName',
    //   align: 'center',
    // }
  ];

  return (
    <div className={styles['card-box']} >
      <Card >
        <Button className={styles['card-button']} fill  onClick={()=>{console.log(item,'item'); setSheetValue(item) ;setSheetOpened(true) ;}} sheetOpen=".demo-sheet-swipe-to-close">
          <ul className={styles['card-ul']} >
            <li>
              <span className={styles['li-title']}>{item.receiptNumber || ''}</span>
              <span
                className={styles['li-status']}
                style={style}
              >
                {state}
              </span>
            </li>
            {columns.map((column, index) => {
              return (
                <li key={index}>
                  <span className={styles['li-next-title']}>{column.title}</span>
                  <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span>
                </li>
              );
            })}
          </ul>
        </Button>
      </Card>

      <Sheet
        className="demo-sheet-swipe-to-close"
        style={{ height: 'auto' }}
        swipeToClose
        push
        backdrop
        opened={sheetOpened}
        onSheetClosed={() => {
          setSheetOpened(false);
        }}
      >
        <div className="swipe-handler"></div>
        <CardSheet item={sheetValue} />
      </Sheet>
    </div>

  );
};

export default CardInfo;
