import React, { useEffect, useState } from 'react';
import { Card, Button } from '@hvisions/f-ui';
import styles from './cardInfo.scss';
import { Sheet } from 'framework7-react';

const CardInfo = ({ item }) => {
  const [style, setStyle] = useState();
  const [state, setState] = useState();
  const [sheetValue, setSheetValue] = useState({});
  const [sheetOpened, setSheetOpened] = useState(false);

  useEffect(() => {
    if (item.state == 0) {
      setStyle(newStyle);
      setState('新建');
    }
    if (item.state == 1) {
      setStyle(onGoingStyle);
      setState('执行中');
    }
    if (item.state == 2) {
      setStyle(completeStyle);
      setState('已完成');
    }
  }, []);

  const newStyle = {
    background: '#ffdad4',
    color: '#d83333'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };
  const completeStyle = {
    background: '#D3F0E9',
    color: '#42BB9E'
  };

  const columns = [
    {
      title: '关联单号',
      dataIndex: 'associatedNumber',
      key: 'associatedNumber',
      align: 'center'
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      key: 'createTime',
      align: 'center'
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center'
    }
  ];

  return (
    <div className={styles['card-box']}>
      <Card>
        <Button
          className={styles['card-button']}
          fill
          onClick={() => {
            setSheetValue(item);
            setSheetOpened(true);
          }}
        >
          <ul className={styles['card-ul']}>
                 <ul className={styles['card-ul']}>
                   <li>
                     <span className={styles['li-next-title']}>托盘号</span>
                     <span className={styles['li-next-title']}>{value.code || ''}</span>
                   </li>
                   <li>
                     <span className={styles['li-next-title']}>任务状态</span>
                     <span className={styles['li-next-title']}>{value.taskStatus || ''}</span>
                   </li>
                   <li>
                     <span className={styles['li-next-title']}>托盘状态</span>
                     <span
                      className={styles['li-status']}
                      style={item.state == '空闲' ? newStyle : onGoingStyle}
                    >
                      {item.state || ''}
                    </span>
                  </li>
                </ul>
                <div className={styles['card-div']}>
                  <Button
                    fill
                    round
                    className={styles['bottom-btn-confirm']}
                    // onClick={handleConfirm}
                  >
                    下架
                  </Button>
                </div>
          </ul>
        </Button>
      </Card>
    </div>
  );
};

export default CardInfo;
