import React, { useEffect, useRef, useState , useCallback } from 'react';
import { Page, Navbar, NavLeft, NavTitle, PageContent, Button, ListInput, List, } from '@hvisions/f-ui';
import styles from './style.scss';
import backIcon from '~/pages/WarehousinManage/img/backIcon.png';
import { onToast } from '~/util/home';
import { isEmpty } from 'lodash';
import CardInfo from './cardInfo';
import { Skeleton, Empty } from '~/components';
import TransferBoxServices from '~/api/TransferBox';

const Manual = ({ f7router }) => {
  const countRef = useRef(10);
  const [loading, setLoading] = useState(false);
  const [list, setList] = useState([]);
  const [total, setTotal] = useState(0);
  const [showPreloader, setShowPreloader] = useState(false);
  const [allowInfinite, setAllowInfinite] = useState(true);
  const [ptrPreloader, setPtrPreloader] = useState(false);

  const inputRef = useRef(null);

  const keyDown = useCallback(e => {
    if (!window.cordova) {
      return;
    }
    window.cordova.define.moduleMap['scanPda.scanPda'].exports.coolMethod('start', function(data) {
      console.log('data',data);
      //handleNext(data);
      // setAllNumbers(cur => {
      //   if (cur.includes(data)) {
      //     return cur;
      //   }
      //   return [...cur, data];
      // });
    });
  }, []);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async keyWord => {
    setLoading(true);
    const searchData = {
      pageSize: countRef.current,
      type: 0,
    };
    await TransferBoxServices.getPage(searchData)
      .then(res => {
        setList(res.content);
        setTotal(res.totalElements);
      })
      .catch(err => {
        onToast(err.message, styles.toastError);
      });
    setLoading(false);
  };

  const loadMore = async () => {
    if (!allowInfinite) return;
    setShowPreloader(true);
    if (countRef.current >= total) {
      setShowPreloader(false);
      return;
    }
    setAllowInfinite(false);
    countRef.current = countRef.current + 10;
    await loadData();
    await setAllowInfinite(true);
  };

  const onHandleRefresh = async done => {
    await setPtrPreloader(true);
    await loadData();
    await setPtrPreloader(false);
    await done();
  };

  const renderCardList = () =>
    !loading ? (
      !isEmpty(list) ? (
        list.map(value => (
          <CardInfo
            key={value.id}
            item={value}
            loadData={loadData}
            f7router={f7router}
          />
        ))
      ) : (
        <Empty />
      )
    ) : (
      <Skeleton />
    );

  const handleSave = () => {

  }

  return (
    <Page pageContent={false}>
      <Navbar>
        <NavLeft>
          <a onClick={() => f7router.back()} className="ne-navleft">
            <img alt="" style={{ height: 24 }} src={backIcon} />
          </a>
        </NavLeft>
        <NavTitle>托盘物料绑定</NavTitle>
      </Navbar>
      <PageContent
        infinite
        infiniteDistance={50}
        infinitePreloader={showPreloader}
        onInfinite={loadMore}
        ptrPreloader={ptrPreloader}
        ptr
        onPtrRefresh={onHandleRefresh}
        onPtrPullStart={() => {
          setPtrPreloader(true);
        }}
      >
        <div style={{ padding: '0 16px' }} className={styles.tabContainer}>

          <List strongIos dividersIos insetIos style={{ padding: '0 16px' }}>
            <ListInput
              key={'addSheet'}
              label="托盘号"
              type="text"
              placeholder="请输入托盘号"
              required
              validate
              clearButton
            // onChange={(e)=>{
            //   const v = createSheetValue
            //   v.tyayNumber = e.target.value
            //   setCreateSheetValue(v)
            //   setTyayNumber(e.target.value)
            // }}
            // value={tyayNumber}
            >

            </ListInput>
          </List>
          <input
            style={{ margin: '16px 16px 16px 16px',width:'80%',height:30}}
            placeholder={"请扫描收料单条码"}
            ref={inputRef}
            readOnly
            onFocus={() => {
              // setInputPlaceHolder('请扫码');
              document.addEventListener('keydown', keyDown);
            }}
            onBlur={() => {
              inputRef.current.value = '';
              // setInputPlaceHolder('请点击进行扫码');
              document.removeEventListener('keydown', keyDown);
            }}
          ></input>
          {/* <input
            label="收料单扫码"
            type="text"
            placeholder="请扫描收料单条码"
            validate
            clearButton
          // onChange={(e)=>{
          //   const v = createSheetValue
          //   v.rawMaterial = e.target.value
          //   setCreateSheetValue(v)
          //   setRawMaterial(e.target.value)
          // }}
          // value={scan}
          >

          </input> */}
          <List strongIos dividersIos insetIos style={{ padding: '0 16px' }}>
            <ListInput
              label="采购订单号"
              type="text"
              placeholder="请输入采购订单号"
              required
              validate
              clearButton
            // onChange={(e)=>{
            //   const v = createSheetValue
            //   v.rawMaterial = e.target.value
            //   setCreateSheetValue(v)
            //   setRawMaterial(e.target.value)
            // }}
            // value={orderNumber}
            >

            </ListInput>
            <ListInput
              label="送货单行号"
              type="text"
              placeholder="请输入送货单行号"
              required
              validate
              clearButton
            // onChange={(e)=>{
            //   const v = createSheetValue
            //   v.rawMaterial = e.target.value
            //   setCreateSheetValue(v)
            //   setRawMaterial(e.target.value)
            // }}
            // value={lineNumber}
            >

            </ListInput>
            <ListInput
              label="物料编码"
              type="text"
              placeholder="请输入物料编码"
              required
              validate
              clearButton
            // onChange={(e)=>{
            //   const v = createSheetValue
            //   v.rawMaterial = e.target.value
            //   setCreateSheetValue(v)
            //   setRawMaterial(e.target.value)
            // }}
            // value={materialCode}
            >

            </ListInput>
            <ListInput
              label="数量"
              type="text"
              placeholder="请输入数量"
              required
              validate
              clearButton
            // onChange={(e)=>{
            //   const v = createSheetValue
            //   v.rawMaterial = e.target.value
            //   setCreateSheetValue(v)
            //   setRawMaterial(e.target.value)
            // }}
            // value={rawMaterial}
            >

            </ListInput>
          </List>
          <div className={styles['detail-bottom']}>
            <Button className={styles['bottom-btn-confirm']} fill round onClick={handleSave}>
              保存
            </Button>
          </div>


        </div>
      </PageContent>
    </Page >
  );
};

export default Manual;
