import React from 'react';
import styles from './style.scss';
import { Page, PageContent, Card, Button, CardContent } from '@hvisions/f-ui';
import classnames from 'classnames';

const ManualSheet = ({ item }) => {
  const columns = [
    {
      title: '托盘号',
      dataIndex: 'code',
      key: 'code',
      align: 'center'
    },
    {
      title: '任务状态',
      dataIndex: 'taskStatus',
      key: 'taskStatus',
      align: 'center'
    },
    {
      title: '托盘状态',
      dataIndex: 'state',
      key: 'state',
      align: 'center'
    }
  ];

  const data = [
    {
      code: '111',
      taskStatus: '222',
      state: '333'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    }
  ];

  return (
    <PageContent
      infinite
      infiniteDistance={50}
    // infinitePreloader={showPreloader}
    // onInfinite={loadMore}
    // ptrPreloader={ptrPreloader}
    // ptr
    // onPtrRefresh={onHandleRefresh}
    // onPtrPullStart={() => {
    //   setPtrPreloader(true);
    // }}
    >
      <div style={{ padding: '0 16px', backgroundColor: '#f4f5f5' }} className={styles.tabContainer}>
        <ul className={styles['div-ul']}>
          <li>
            <span className={styles['li-title']}>{'托盘手动下架'}</span>
          </li>
          {data.map((value, index) => {
            return (
              <Card key={index} className={styles['card']} >
                <ul className={styles['card-ul']}>
                  <li>
                    <span className={styles['li-next-title']}>托盘号</span>
                    <span className={styles['li-next-title']}>{value.code || ''}</span>
                  </li>
                  <li>
                    <span className={styles['li-next-title']}>任务状态</span>
                    <span className={styles['li-next-title']}>{value.taskStatus || ''}</span>
                  </li>
                  <li>
                    <span className={styles['li-next-title']}>托盘状态</span>
                    <span className={styles['li-next-title']}>{value.state || ''}</span>
                  </li>
                </ul>
                <div>
                <Button
                fill
                round
                className={styles['bottom-btn-confirm']}
                // onClick={handleConfirm}
              >
                下架
              </Button>
              </div>
              </Card>
            );
          })}
        </ul>
      </div>
    </PageContent>
  );
};

export default ManualSheet;
