  //     });
  // };

  // const onHandleDelete = async record => {
  //   const id = record.id;
  //   console.log('id', id);
  //   await PutMaterialService.deleteSaleLine(id)
  //     .then(res => {
  //       const searchValue = {};

  //       if (selectedMaterial?.value !== undefined) {
