import React, { useEffect, useState, useRef } from 'react';
import { Card, CardContent, Button } from '@hvisions/f-ui';
import { i18n, page } from '@hvisions/toolkit';
import styles from './style.scss';
import { Sheet, PageContent, List, ListItem, Block, } from 'framework7-react';
const getFormattedMsg = i18n.getFormattedMsg;

const CardSheet = ({
  f7router,
  item,
  loadData,
  handleDelete,
  onHandleDelete
}) => {
  const [sheetValue, setSheetValue] = useState({});

  const sheet = useRef(null);

  useEffect(() => {
  }, []);;

  const cardColumns = [
    // {
    //   title: '收料单号',
    //   dataIndex: 'receiptNumber',
    //   key: 'receiptNumber',
    //   align: 'center',
    // },
    {
      title: '行号',
      dataIndex: 'lineNumber',
      key: 'lineNumber',
      align: 'center',
    },

    {
      title: '关联单号',
      dataIndex: 'associatedNumber',
      key: 'associatedNumber',
      align: 'center',
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      key: 'createTime',
      align: 'center',
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center',
    },
    {
      title: '单据数量',
      dataIndex: 'number',
      key: 'number',
      align: 'center',
    },
    {
      title: '单据物料编码',
      dataIndex: 'materialCode',
      key: 'materialCode',
      align: 'center',
    },
    {
      title: '单据物料名称',
      dataIndex: 'materialName',
      key: 'materialName',
      align: 'center',
    },
    {
      title: '托盘号',
      dataIndex: 'trayNumber',
      key: 'trayNumber',
      align: 'center',
    },
    {
      title: '规格',
      dataIndex: 'specifications',
      key: 'specifications',
      align: 'center',
    },
    {
      title: '实际重量',
      dataIndex: 'actualWeight',
      key: 'actualWeight',
      align: 'center',
    },
    {
      title: '实际数量',
      dataIndex: 'actualNumber',
      key: 'actualNumber',
      align: 'center',
    },
    {
      title: '实际物料编码',
      dataIndex: 'realityMaterialCode',
      key: 'realityMaterialCode',
      align: 'center',
    },
    {
      title: '实际物料名称',
      dataIndex: 'realityMaterialName',
      key: 'realityMaterialName',
      align: 'center',
    }
  ];

  return (
      <Sheet
        className="demo-sheet-swipe-to-close"
        style={{ height: 'auto' }}
        swipeToClose
        push
        backdrop
      >
        <div className="swipe-handler"></div>

        <PageContent>
          <ul className={styles['card-ul']} >
            <li>
              <span className={styles['li-title']}>{sheetValue.receiptNumber || ''}</span>
            </li>
            <List>
              {cardColumns.map((column, index) => {
                return (
                  <ListItem key={index}>
                      <span className={styles['li-next-title']}>{column.title}&nbsp;:&nbsp;&nbsp;&nbsp;</span>
                      <span className={styles['li-next-title']}>{sheetValue[column.dataIndex] || ''}</span>
                  </ListItem>
                );
              })}
            </List>
          </ul>
        </PageContent>
      </Sheet>
  );
};

export default CardSheet;
