import React, { useEffect, useState } from 'react';
import { Card, Button } from '@hvisions/f-ui';
import styles from './cardInfo.scss';
import { Sheet } from 'framework7-react';
import CardSheet from './CardSheet/index';

const CardInfo = ({ 
  item, 
  setOutSheetOpen,
  setOutSheetData,
  getReadyMaterialList,
}) => {
  const [style, setStyle] = useState();
  const [state, setState] = useState();
  const [sheetValue, setSheetValue] = useState({});
  const [sheetOpened, setSheetOpened] = useState(false);

  const columns = [
    {
      title: '折弯机名称',
      dataIndex: 'bendingName',
      key: 'bendingName',
      align: 'center',
    },
    {
      title: '折弯机状态',
      dataIndex: 'bendingState',
      key: 'bendingState',
      align: 'center',
    },
    {
      title: '折弯机属性',
      dataIndex: 'attributeName',
      key: 'attributeName',
      align: 'center',
    },
    {
      title: '是否允许折弯未完成出库',
      dataIndex: 'ifoutName',
      key: 'ifoutName',
      align: 'center',
    },
    {
      title: '最小在库时间',
      dataIndex: 'warhouseTime',
      key: 'warhouseTime',
      align: 'center',
    },
    {
      title: '备料区',
      dataIndex: 'readyMaterials',
      key: 'readyMaterials',
      align: 'center',
    },
    {
      title: '托盘号',
      dataIndex: 'transferCode',
      key: 'transferCode',
      align: 'center',
    },
  ];

  return (
    <div className={styles['card-box']}>
      <Card className={styles['card']}>
        <ul className={styles['card-ul']}>
            <li>
              <span className={styles['li-title']}>{item.bendingNumber || ''}</span>
              <span
              className={styles['li-status']}
              style={style}
            >
              {state}
            </span>
            </li>
            {columns.map((column, index) => {
              return (
                <li key={index}>
                  <span className={styles['li-next-title']}>{column.title}</span>
                  <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span>
                </li>
              );
            })}
        </ul>
        <div className={styles['card-div']}>
          <ul className={styles['div-ul']}>
            <Button
              fill
              round
              className={styles['bottom-btn-confirm']}
              style={{ margin: "5px" }}
              onClick={() => {
                getReadyMaterialList()
                setOutSheetOpen(true);
                setOutSheetData(item);                
              }}>
              出库
            </Button>
          </ul>
        </div>
      </Card>
    </div>
  );
};

export default CardInfo;
