import React, { useEffect, useState } from 'react';
import { Card, Button } from '@hvisions/f-ui';
import styles from './cardInfo.scss';
import { Sheet } from 'framework7-react';
// import CardSheet from './CardSheet/index';

const CardInfo = ({ f7router, item, handleWarehousing, }) => {
  const [style, setStyle] = useState();
  const [state, setState] = useState();
  const [sheetValue, setSheetValue] = useState({});
  const [sheetOpened, setSheetOpened] = useState(false);

  useEffect(() => {
    if (item.state == 0) {
      setStyle(newStyle);
      setState('新建');
    }
    if (item.state == 1) {
      setStyle(onGoingStyle);
      setState('执行中');
    }
    if (item.state == 2) {
      setStyle(completeStyle);
      setState('已完成');
    }
  }, []);

  const newStyle = {
    background: '#ffdad4',
    color: '#d83333'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };
  const completeStyle = {
    background: '#D3F0E9',
    color: '#42BB9E'
  };

  const columns = [
    // {
    //   title: '订单号',
    //   dataIndex: 'orderNumber',
    //   key: 'orderNumber',
    //   align: 'center',
    // },
    {
      title: '托盘号',
      dataIndex: 'trayNumber',
      key: 'trayNumber',
      align: 'center',
    },
    {
      title: '产品名称',
      dataIndex: 'materialName',
      key: 'materialName',
      align: 'center',
    },
    {
      title: '产品代码',
      dataIndex: 'materialCode',
      key: 'materialCode',
      align: 'center',
    },
    {
      title: '产品数量(张)',
      dataIndex: 'quantity',
      key: 'quantity',
      align: 'center',
    },
    {
      title: '属性1',
      dataIndex: 'attributeOne',
      key: 'attributeOne',
      align: 'center',
    },
    {
      title: '属性2',
      dataIndex: 'attributeTwo',
      key: 'attributeTwo',
      align: 'center',
    },
    {
      title: '备注',
      dataIndex: 'desc',
      key: 'desc',
      align: 'center',
    },
  ]

  return (
    <div className={styles['card-box']}>
      <Card className={styles['card']}>
        <ul className={styles['card-ul']}>
          <Button
            className={styles['card-button']}
            fill
          >
            <li>
              <span className={styles['li-title']}>{item.orderNumber || ''}</span>
              <span className={styles['li-status']} style={style}>
                {state}
              </span>
            </li>
            {columns.map((column, index) => {
              return (
                <li key={index}>
                  <span className={styles['li-next-title']}>{column.title}</span>
                  <span className={styles['li-next-title']} style={{ overflow: 'hidden', textOverflow: 'ellipsis', width: 150, textAlign: 'end' }}>{item[column.dataIndex] || ''}</span>
                </li>
              );
            })}
          </Button>
        </ul>
        <ul className={styles['div-ul']}>
          <Button
            fill
            round
            className={styles['weighing-button']}
            onClick={() => handleWarehousing(item)}
          >
            <span style={{ padding: "10px" }}>入库</span>
          </Button>
        </ul>
      </Card>
    </div>
  );
};

export default CardInfo;
