import React, { useEffect, useState } from 'react';
import { Card, Button } from '@hvisions/f-ui';
import styles from './cardInfo.scss';
import { Sheet } from 'framework7-react';
import CardSheet from './CardSheet/index';

const CardInfo = ({ 
  item, 
  tabKey,
  setAdjustSheetOpen,
  setAdjustSheetData 
}) => {

  const columns = [
    {
      title: '任务类型',
      dataIndex: 'taskType',
      key: 'taskType',
      align: 'center'
    },
    {
      title: '托盘号',
      dataIndex: 'transferCode',
      key: 'transferCode',
      align: 'center'
    },
    {
      title: '起始位置',
      dataIndex: 'fromLocation',
      key: 'fromLocation',
      align: 'center'
    },
    {
      title: '中间位置',
      dataIndex: 'middle',
      key: 'middle',
      align: 'center'
    },
    {
      title: '目标位置',
      dataIndex: 'toLocation',
      key: 'toLocation',
      align: 'center'
    },
    {
      title: '优先级',
      dataIndex: 'priority',
      key: 'priority',
      align: 'center'
    },
  ];

  return (
    <div className={styles['card-box']}>
      <Card className={styles['card']}>
        <ul className={styles['card-ul']}>
            <li>
              <span className={styles['li-title']}>{item.taskCode || ''}</span>
            </li>
            {columns.map((column, index) => {
              return (
                <li key={index}>
                  <span className={styles['li-next-title']}>{column.title}</span>
                  <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span>
                </li>
              );
            })}
          
        </ul>
        <div className={styles['card-div']}>
          <ul className={styles['div-ul']}>
          {tabKey == 1&&<Button key="adjust" fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }} onClick={()=>{}}>
              调整优先级
            </Button>}
            {tabKey == 2&&<Button key="pause" fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }} onClick={()=>{}}>
              暂停
            </Button>}
            {(tabKey == 2 ||tabKey == 5) &&<Button key="complete" fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }} onClick={()=>{}}>
              完成
            </Button>}
            {tabKey == 3&&<Button key="continue" fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }} onClick={()=>{}}>
              继续
            </Button>}
            {tabKey == 5&&<Button key="rollback" fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }} onClick={()=>{}}>
              回退
            </Button>}
          </ul>
      </div>
      </Card>
    </div>
  );
};

export default CardInfo;
