import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Page, Navbar, NavLeft, NavTitle, PageContent, Button, ListInput, List, Card, Input,ListItem } from '@hvisions/f-ui';
import styles from './style.scss';
import backIcon from '~/pages/WarehousinManage/img/backIcon.png';
import { onToast } from '~/util/home';
import { isEmpty } from 'lodash';
import { Skeleton, Empty } from '~/components';
import TransferBoxServices from '~/api/TransferBox';
import joinAreaServices from '~/api/joinArea';
import RawMaterialWarehousingApi from '~/api/RawMaterialWarehousing';

import PrepareAreaApi from '~/api/PrepareArea';

const sortPosition = [
  { id: 1, name: 'J004', value: 'J004', },
  { id: 2, name: 'J005', value: 'J005', },
  { id: 3, name: 'J006', value: 'J006', },
]

const attributeOnes = [
  { id: 1, name: '大', value: '大', },
  { id: 2, name: '中', value: '中', },
  { id: 3, name: '小', value: '小', },
]

const attributeTwos = [
  { id: 1, name: '切割完工', value: '切割完工', },
  { id: 2, name: '切割未完工', value: '切割未完工', },
]

const dockingPoints = [
  { id: 1, name: 'J002', value: 'J002', },
  { id: 2, name: 'J003', value: 'J003', },
]

const SemiFinishedBinding1 = ({ f7router, trayNumber }) => {
  const countRef = useRef(10);
  const [loading, setLoading] = useState(false);
  const [list, setList] = useState([]);
  const [total, setTotal] = useState(0);
  const [showPreloader, setShowPreloader] = useState(false);
  const [allowInfinite, setAllowInfinite] = useState(true);
  const [ptrPreloader, setPtrPreloader] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const [formData, setFormData] = useState({ trayNumber: trayNumber });
  const [orderNumber, setOrderNumber] = useState('');
  const [lineNumber, setLineNumber] = useState('');
  const [materialCode, setMaterialCode] = useState('');
  const [number, setNumber] = useState('');

  const inputRef = useRef(null);

  useEffect(() => {

  }, [formData, trayNumber]);

  const onKeyDowm = (e) => {

    const dataString = inputValue
    const arr = dataString.split('|');
    console.log('arr', arr);

    const orderNumber = arr[0]
    const lineNumber = arr[1]
    const associatedNumber = arr[2]
    const other = arr[3]
    const materialCode = arr[4]
    const number = arr[5]
    const unit = arr[6]

    setFormData({
      orderNumber: orderNumber,
      lineNumber: lineNumber,
      materialCode: materialCode,
      number: Number(number),
      associatedNumber: associatedNumber,
      unit: unit,
    })
    setOrderNumber(orderNumber);
    setLineNumber(lineNumber);
    setMaterialCode(materialCode);
    setNumber(number);
    setInputValue('')
  }

  const handleSave = async () => {
    console.log('formData', formData);
    formData.associatedNumber = formData.orderNumber
    await RawMaterialWarehousingApi
      .bindRawMaterial(formData)
      .then(res => {
        onToast('物料绑定成功', styles.toastSuccess);
        handleGoBack()
      })
      .catch(err => {
        onToast(err.message, styles.toastError);
      });
  }

  const handleGoBack = () => {
    f7router.navigate('/raw-material-warehousing', {
      transition: 'ne-backward',
    });
  };



  const sortPositionChange = async (e) => {
    const sortPosition = e
    console.log('sortPosition', sortPosition);
    await PrepareAreaApi.findByArea({ areaCode: sortPosition })
      .then(res => {
        //console.log(res, 'res');
        if (res.content.length > 0) {
          const transferCode = res.content[0].transferCode
          // setFieldsValue({ trayNumber: transferCode })
        }
      })
    // .catch(err => {
    //   notification.warning({
    //     description: err.message
    //   });
    // });
  }



  return (
    <Page pageContent={false}>
      <Navbar>
        <NavLeft>
          <a onClick={() => f7router.back()} className="ne-navleft">
            <img alt="" style={{ height: 24 }} src={backIcon} />
          </a>
        </NavLeft>
        <NavTitle>半成品绑定</NavTitle>
      </Navbar>
      <PageContent
        infinite
        infiniteDistance={50}
        infinitePreloader={showPreloader}
        ptrPreloader={ptrPreloader}
        ptr
        onPtrPullStart={() => {
          setPtrPreloader(true);
        }}
      >
        <div style={{ padding: '0 16px 56px 16px' }} className={styles.tabContainer}>
        <List strongIos outlineIos dividersIos>
            <ListItem title="Fruit" smartSelect>
              <select name="fruits" defaultValue={undefined} autoComplete="off">
              <option selected="selected" disabled="disabled"  style='display: none' value=''></option> 
                <option key='0' value="">-11111-请选择分拣位置--</option>
                <option key='1' value="J004">J004</option>
                <option key='2' value="J005">J005</option>
                <option key='3' value="J006">J006</option>
              </select>
            </ListItem>
          </List>
          {/* <List strongIos dividersIos insetIos style={{ padding: '0 16px' }}>

            <ListItem title="Mac or Windows" smartSelect  >
              <select name="pets" id="pet-select">
                <option key='0' value="">--请选择分拣位置--</option>
                <option key='1' value="J004">J004</option>
                <option key='2' value="J005">J005</option>
                <option key='3' value="J006">J006</option>
              </select>
            </ListItem>

            <ListInput
              label="分拣位置"
              type="select"
              placeholder="请选择分拣位置"
              required
              validate
              clearButton
              onChange={(e) => {
                const v = formData
                v.sortPosition = e.target.value
                setFormData(v)
                sortPositionChange(e)
              }}
            >
              {sortPosition.map((value, index) => (
                <option value={value.value} key={value.id}>
                  {value.name}
                </option>
              ))}
            </ListInput>
            <ListInput
              label="托盘号"
              type="text"
              placeholder="请输入托盘号"
              required
              validate
              clearButton
              onChange={(e) => {
                const v = formData
                v.trayNumber = e.target.value
                setFormData(v)
              }}
              defaultValue={formData.trayNumber}
            >
            </ListInput>
            <ListInput
              label="订单信息"
              type="text"
              placeholder="请输入订单信息"
              required
              validate
              clearButton
              onChange={(e) => {
                const v = formData
                v.lineNumber = e.target.value
                setFormData(v)
              }}
            // value={formData.lineNumber}
            // defaultValue={formData.lineNumber}
            >
            </ListInput>
            <ListInput
              label="折弯属性"
              type="select"
              placeholder="请输入折弯属性"
              required
              validate
              clearButton
              multiple={true}
              onChange={(e) => {
                const v = formData
                v.attributeOne = e.target.value
                setFormData(v)
              }}
              defaultValue={formData.attributeOne}
            >
              {attributeOnes.map((value, index) => (
                <option value={value.value} key={value.id}>
                  {value.name}
                </option>
              ))}
            </ListInput>
            <ListInput
              label="完工属性"
              type="select"
              placeholder="请输入完工属性"
              required
              validate
              clearButton
              onChange={(e) => {
                const v = formData
                v.attributeTwo = e.target.value
                setFormData(v)
              }}
              defaultValue={formData.attributeTwo}
            >
              {attributeTwos.map((value, index) => (
                <option value={value.value} key={value.id}>
                  {value.name}
                </option>
              ))}
            </ListInput>
            <ListInput
              label="接驳点"
              type="select"
              placeholder="请输入接驳点"
              required
              validate
              clearButton
              onChange={(e) => {
                const v = formData
                v.dockingPoint = e.target.value
                setFormData(v)
              }}
              defaultValue={formData.dockingPoint}
            >
              {dockingPoints.map((value, index) => (
                <option value={value.value} key={value.id}>
                  {value.name}
                </option>
              ))}
            </ListInput>
            <ListInput
              label="备注"
              type="text"
              placeholder="请输入备注"
              required
              validate
              clearButton
              onChange={(e) => {
                const v = formData
                v.desc = e.target.value
                setFormData(v)
              }}
              defaultValue={formData.desc}
            >
            </ListInput>
          </List> */}
        </div>
      </PageContent>
      <div className={styles['detail-bottom']}>
        <Button className={styles['bottom-btn-confirm']} fill round onClick={handleSave}>
          保存
        </Button>
      </div>
    </Page >
  );
};

export default SemiFinishedBinding1;
