import React from 'react';
import { Card, CardContent, Button } from '@hvisions/f-ui';
import { i18n, page } from '@hvisions/toolkit';
import styles from './cardInfo.scss';

const getFormattedMsg = i18n.getFormattedMsg;

const CardInfo = ({
  f7router,
  item,
  loadData,
  handleDelete,
  orderLineId,
  state,
  onHandleDelete
}) => {
  const newStyle = {
    background: '#c9758d',
    color: '#f27387'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };
  const completeStyle = {
    background: '#D3F0E9',
    color: '#42BB9E'
  };

  const columns = [
    // {
    //   title: '收料单号',
    //   dataIndex: 'receiptNumber',
    //   key: 'receiptNumber',
    //   align: 'center',
    // },
    // {
    //   title: '行号',
    //   dataIndex: 'lineNumber',
    //   key: 'lineNumber',
    //   align: 'center',
    // },

    {
      title: '关联单号',
      dataIndex: 'associatedNumber',
      key: 'associatedNumber',
      align: 'center',
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      key: 'createTime',
      align: 'center',
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center',
    },
    // {
    //   title: '单据数量',
    //   dataIndex: 'number',
    //   key: 'number',
    //   align: 'center',
    // },
    // {
    //   title: '单据物料编码',
    //   dataIndex: 'materialCode',
    //   key: 'materialCode',
    //   align: 'center',
    // },
    // {
    //   title: '单据物料名称',
    //   dataIndex: 'materialName',
    //   key: 'materialName',
    //   align: 'center',
    // },
    // {
    //   title: '托盘号',
    //   dataIndex: 'trayNumber',
    //   key: 'trayNumber',
    //   align: 'center',
    // },
    // {
    //   title: '规格',
    //   dataIndex: 'specifications',
    //   key: 'specifications',
    //   align: 'center',
    // },
    // {
    //   title: '实际重量',
    //   dataIndex: 'actualWeight',
    //   key: 'actualWeight',
    //   align: 'center',
    // },
    // {
    //   title: '实际数量',
    //   dataIndex: 'actualNumber',
    //   key: 'actualNumber',
    //   align: 'center',
    // },
    // {
    //   title: '实际物料编码',
    //   dataIndex: 'realityMaterialCode',
    //   key: 'realityMaterialCode',
    //   align: 'center',
    // },
    // {
    //   title: '实际物料名称',
    //   dataIndex: 'realityMaterialName',
    //   key: 'realityMaterialName',
    //   align: 'center',
    // }
  ];

  return (
    <div className={styles['card-box']}>
      <Card>
        <ul className={styles['card-ul']}>
        <li>
            <span className={styles['li-title']}>{item.receiptNumber || ''}</span>
            <span
              className={styles['li-status']}
              // style={item.state == 1 ? onGoingStyle : completeStyle}
              style={()=>{
                if(item.state == 0){ return onGoingStyle}
                if(item.state == 1){ return onGoingStyle}
                if(item.state == 1){ return onGoingStyle}
              }}
            >
              {item.state == 1 ? '执行中' : '已完成'}
            </span>
          </li>
          {columns.map((column, index) => {
            return (
              <li key={index}>
                <span className={styles['li-next-title']}>{column.title}</span>
                <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span>
              </li>
            );
          })}

          <li className={styles['li-button']}>
            <Button
              fill
              round
              className={styles['delete-button']}
              onClick={() => onHandleDelete(item)}
            >
              删除
            </Button>
          </li>
        </ul>
      </Card>
    </div>
  );
};

export default CardInfo;
