import React from 'react';
import styles from './style.scss';
import { PageContent, Card, ListItem } from 'framework7-react';

const ManualSheet = ({ item }) => {
  const columns = [
    // {
    //   title: '收料单号',
    //   dataIndex: 'receiptNumber',
    //   key: 'receiptNumber',
    //   align: 'center',
    // },
    {
      title: '行号',
      dataIndex: 'lineNumber',
      key: 'lineNumber',
      align: 'center'
    },

    {
      title: '关联单号',
      dataIndex: 'associatedNumber',
      key: 'associatedNumber',
      align: 'center'
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      key: 'createTime',
      align: 'center'
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center'
    },
    {
      title: '单据数量',
      dataIndex: 'number',
      key: 'number',
      align: 'center'
    },
    {
      title: '单据物料编码',
      dataIndex: 'materialCode',
      key: 'materialCode',
      align: 'center'
    },
    {
      title: '单据物料名称',
      dataIndex: 'materialName',
      key: 'materialName',
      align: 'center'
    },
    {
      title: '托盘号',
      dataIndex: 'trayNumber',
      key: 'trayNumber',
      align: 'center'
    },
    {
      title: '规格',
      dataIndex: 'specifications',
      key: 'specifications',
      align: 'center'
    },
    {
      title: '实际重量',
      dataIndex: 'actualWeight',
      key: 'actualWeight',
      align: 'center'
    },
    {
      title: '实际数量',
      dataIndex: 'actualNumber',
      key: 'actualNumber',
      align: 'center'
    },
    {
      title: '实际物料编码',
      dataIndex: 'realityMaterialCode',
      key: 'realityMaterialCode',
      align: 'center'
    },
    {
      title: '实际物料名称',
      dataIndex: 'realityMaterialName',
      key: 'realityMaterialName',
      align: 'center'
    }
  ];

  return (
    <PageContent
      infinite
      infiniteDistance={50}
    // infinitePreloader={showPreloader}
    // onInfinite={loadMore}
    // ptrPreloader={ptrPreloader}
    // ptr
    // onPtrRefresh={onHandleRefresh}
    // onPtrPullStart={() => {
    //   setPtrPreloader(true);
    // }}
    >
      <div style={{ padding: '0 16px' }} className={styles.tabContainer}>
        <ul className={styles['card-ul']}>
          <li>
            <span className={styles['li-title']}>{'托盘手动下架'}</span>
          </li>
          <Card>
            {columns.map((column, index) => {
              return (
                <li key={index}>
                  <span className={styles['li-next-title']}>
                    {column.title}&nbsp;:&nbsp;&nbsp;&nbsp;
                  </span>
                  {/* <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span> */}
                </li>
              );
            })}
          </Card>
        </ul>
      </div>
    </PageContent>
  );
};

export default ManualSheet;
