import React, { useEffect, useState } from 'react';
import { Card, Button } from '@hvisions/f-ui';
import styles from './cardInfo.scss';
import { Sheet } from 'framework7-react';
import CardSheet from './CardSheet/index';
import { attributeOne,attributeTwo,dockingPoints,sortPositions } from '~/enum/enum';

const CardInfo = ({ item, handleWeighing,handleWarehousing }) => {
  const [style, setStyle] = useState();
  const [state, setState] = useState();
  const [sheetValue, setSheetValue] = useState({});
  const [sheetOpened, setSheetOpened] = useState(false);

  useEffect(() => {
    formatOther(item)
  }, []);

  const columns = [
    {
      title: '创建时间',
      dataIndex: 'createTime',
      key: 'createTime',
      align: 'center',
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center',
    },
    {
      title: '修改时间',
      dataIndex: 'updateTime',
      key: 'updateTime',
      align: 'center',
    },
    {
      title: '修改人',
      dataIndex: 'updateCreator',
      key: 'updateCreator',
      align: 'center',
    },
    {
      title: '托盘号',
      dataIndex: 'trayNumber',
      key: 'trayNumber',
      align: 'center',
    },
    {
      title: '订单数量',
      dataIndex: 'orderCount',
      key: 'orderCount',
      align: 'center',
    },
    {
      title: '属性1',
      dataIndex: 'attributeOne',
      key: 'attributeOne',
      align: 'center',
    },
    {
      title: '属性2',
      dataIndex: 'attributeTwo',
      key: 'attributeTwo',
      align: 'center',
    },
    {
      title: '接驳点',
      dataIndex: 'dockingPoint',
      key: 'dockingPoint',
      align: 'center',
    },
    {
      title: '分拣位置',
      dataIndex: 'sortPosition',
      key: 'sortPosition',
      align: 'center',
    },
  ];

  const formatAttributeOne = (item) =>{
    const text = item.attributeOne
    if(text == null){
      return
    }
    let array = []
    const arr = text.split(',');
    arr.map(i => {
      array = [...array, attributeOne[i - 1].name]
    })
    item.attributeOneName = array.toString()
  }

  const formatAttributeTwo = (item) =>{
    const text = item.attributeTwo
    if(text == null){
      return
    }
    item.attributeTwoName = attributeTwo[text - 1].name
  }

  const formatOther =(item)=>{
    const text = item.attributeOne
    let array = []
    const arr = text.split(',');
    arr.map(i => {
      array = [...array, attributeOne[i - 1].name]
    })
    item.attributeOneName = text != null ?array.toString():''

    const text2 = item.attributeTwo
    item.attributeTwoName = text2 != null ?attributeTwo[text2 - 1].name:''

    const text3 = item.dockingPoint
    item.dockingPointName = dockingPoints[text3 - 1].name

    const text4 = item.sortPosition
    item.dockingPointName = sortPositions[text4 - 1].name
  }

  return (
    <div className={styles['card-box']}>
      <Card>
        <ul className={styles['card-ul']}>
          <Button
            className={styles['card-button']}
            fill
            onClick={() => {
              setSheetValue(item);
              setSheetOpened(true);
            }}
          >
            <li>
              <span className={styles['li-title']}>{item.receiptNumber || ''}</span>
            </li>
            {columns.map((column, index) => {
              return (
                <li key={index}>
                  <span className={styles['li-next-title']}>{column.title}</span>
                  <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span>
                </li>
              );
            })}
          </Button>
        </ul>
      </Card>
      <Sheet
        style={{ height: '500px' }}
        swipeToClose
        push
        backdrop
        opened={sheetOpened}
        onSheetClosed={() => {
          setSheetOpened(false);
        }}
      >
        <div className="swipe-handler"></div>
        <CardSheet item={sheetValue} />
      </Sheet>
    </div>
  );
};

export default CardInfo;
