import React, { useEffect, useMemo, useState, useRef } from 'react';
import {
  Page,
  Navbar,
  PageContent,
  Popup,
  Link,
  NavRight,
  Searchbar,
  NavLeft,
  NavTitle,
  Toggle,
  Card,
  CardContent,
  Input,
  Toolbar,
  Tabs,
  Tab,
  Button
} from '@hvisions/f-ui';
import { Skeleton, Empty } from '~/components';
import { isEmpty } from 'lodash';
import 'react-circular-progressbar/dist/styles.css';
import backIcon from '../../../../img/backIcon.png';
import styles from './style.scss';
import { goBack, onToast, createDialog } from '~/util/home';
import CardInfo from './CardInfo';

const Master1 = ({
  $f7,
  f7router,
  orderInfo,
}) => {
  const [list, setList] = useState([]);
  const [showPreloader, setShowPreloader] = useState(false);
  const [ptrPreloader, setPtrPreloader] = useState(false);


  useEffect(() => {
    console.log(orderInfo, 'orderInfo');
  }, [orderInfo]);


  const renderCardList = () =>
    !isEmpty(orderInfo) ? (
      orderInfo.map(value => (
        <CardInfo
          key={value.id}
          item={value}
          f7router={f7router}
        />
      ))
    ) : (
      <Empty />
    )

  const handleMasterAdd = () => {
    f7router.navigate('/semi-warehousing-binding-master-add1', {
      props: {
        orderInfo,
      }
    });
  };

  const handleGoBack = () => {
    f7router.navigate('/semi-warehousing-binding1', {
      transition: 'ne-backward',
      props: {
        orderInfo,
      }
    });
  };

  return (
    <Page
      infinite
      infiniteDistance={50}
      infinitePreloader={showPreloader}
      ptrPreloader={ptrPreloader}
      // ptr
      // onPtrPullStart={() => {
      //   setPtrPreloader(true);
      // }}
      className={styles.pageContainer}
    >
      <Navbar>
        <NavLeft>
          {/* <a onClick={() => goBack('/semi-warehousing-binding1')} className={styles['nav-left']}> */}
          <a onClick={() => handleGoBack()} className={styles['nav-left']}>
            <img alt="" style={{ height: 24 }} src={backIcon} />
          </a>
        </NavLeft>
        <NavTitle>主订单信息</NavTitle>
      </Navbar>
      <div className={styles.tabContainer} style={{ padding: '0 16px' }}>
        {renderCardList()}
      </div>
      <div className={styles['detail-bottom']}>
        <Button className={styles['bottom-btn-confirm']} fill round onClick={handleMasterAdd}>
          新增
        </Button>
      </div>
    </Page>
  );
};

export default Master1;
