import React, { useEffect, useState } from 'react';
import { Card, Button } from '@hvisions/f-ui';
import styles from './cardInfo.scss';
import { onToast, createDialog } from '~/util/home';
import { Sheet } from 'framework7-react';

const CardInfo = ({ item }) => {
  const newStyle = {
    background: '#ffdad4',
    color: '#d83333'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };

  
  const handleAutomatic = ()=>{
    createDialog(
      '托盘自动下架',
      '确认开始托盘自动下架流程？',
      function() {
        try {
          Automatic()
          // onToast('托盘自动下架成功', styles.toastSuccess);
        } catch (error) {
          console.log('error',error);
          onToast('托盘自动下架失败', styles.toastError);
        }
      }
    );
  }

  const Automatic = async () => {
    // const data ={
    //   destination:'原材料组托点',
    //   middle:'J001',
    //   taskType:6, //原料托盘出库
    //   transferType:0 //原料托盘
    // }
    // await EmptyPalletDeliveryApi.autoTransferOut(data)
    // .then(res=>{
    //   onToast('托盘自动下架成功', styles.toastSuccess);
    //   loadData(selectValue);
    // })
    // .catch(err=>{
    //   onToast(err.message, styles.toastError);
    // })
    // //托盘出库   托盘自动出库
  }

  return (
      <Card className={styles['card']}>
 
                 <ul className={styles['card-ul']}>
                   <li>
                     <span className={styles['li-next-title']}>托盘号</span>
                     <span className={styles['li-next-title']}>{item.code || ''}</span>
                   </li>
                   <li>
                     <span className={styles['li-next-title']}>任务状态</span>
                     <span className={styles['li-next-title']}>{item.taskStatus || ''}</span>
                   </li>
                   <li>
                     <span className={styles['li-next-title']}>托盘状态</span>
                     <span
                      className={styles['li-status']}
                      style={item.state == '空闲' ? newStyle : onGoingStyle}
                    >
                      {item.state || ''}
                    </span>
                  </li>
                </ul>
                <div className={styles['card-div']}>
                  <Button
                    fill
                    round
                    className={styles['bottom-btn-confirm']}
                    // onClick={handleConfirm}
                  >
                    下架
                  </Button>
                </div>
 
      </Card>

  );
};

export default CardInfo;
