import React, { useEffect, useState } from 'react';
import { Card, CardContent, Button ,Checkbox,Sheet ,BlockTitle} from '@hvisions/f-ui';

import styles from './CardInfo.scss';
import { getAuthData } from '@hvisions/toolkit/lib/session';
import classnames from 'classnames';
const CardInfo = ({ f7router, item, loadData, handleDelete, handleInStorage }) => {

  const [style, setStyle] = useState();
  const [state, setState] = useState();
  const [createSheetOpen, setCreateSheetOpen] = useState(false);

  useEffect(() => {
    if (item.areaState == 0) {
      setStyle(newStyle);
      setState('空置');
    }
    if (item.areaState == 1) {
      setStyle(onGoingStyle);
      setState('使用中');
    }
    if (item.areaState == 2) {
      setStyle(completeStyle);
      setState('运输中');
    }
  }, []);

  const newStyle = {
    background: '#ffdad4',
    color: '#d83333'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };
  const completeStyle = {
    background: '#D3F0E9',
    color: '#42BB9E'
  };

  const onHandleClick = () => {
    f7router.navigate('/inStorage-operate', {
      props: { purchaseNo: item.receiptNumber, orderLineId: item.id, state: item.state }
    });
  };
  const handleDeleteOrder = e => {
    e.stopPropagation();
    handleDelete(item);
  };
  const handleConfirm = e => {
    e.stopPropagation();
    handleInStorage(item);
  };

  const addSheetClosed =()=>{
    setCreateSheetOpen(false);

  }

  return (
    <div className={styles['card-box']}>
      <Card className={styles['card']}>
        <ul className={styles['card-ul']}>
          <li>
            <span className={styles['li-title']}>{item.areaCode || ''}</span>
            <span
              className={styles['li-status']}
              style={style}
            >
              {state}
            </span>
          </li>
          <li>
            <span className={styles['li-next-title']}>托盘号</span>
            <span className={styles['li-next-title']}>{item.transferCode || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>任务状态</span>
            <span className={styles['li-next-title']}>{item.taskState || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>接驳口</span>
            <span className={styles['li-next-title']}>{item.joinArea || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>设备名称</span>
            <span className={styles['li-next-title']}>{item.equipmentName || ''}</span>
          </li>
        </ul>
        <div className={styles['card-div']}>
          <ul className={styles['div-ul']}>
            <Button fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }} onClick>
              绑定托盘
            </Button>
            <Button fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }}>
              更新状态
            </Button>
            <Checkbox name="checkbox-1" style={{ margin: "5px" }} />自动下架托盘
          </ul>
      </div>
      </Card>
      <Sheet
        className={styles['add-sheet']}
        opened={createSheetOpen}
        onSheetClosed={addSheetClosed}
        backdrop
      >
        <BlockTitle>托盘物料绑定</BlockTitle>
