import React from 'react';
import styles from './style.scss';
import { Page,PageContent, Card, CardHeader, CardContent } from '@hvisions/f-ui';

const ManualSheet = ({ item }) => {
  const columns = [
    {
      title: '托盘号',
      dataIndex: 'code',
      key: 'code',
      align: 'center'
    },
    {
      title: '任务状态',
      dataIndex: 'taskStatus',
      key: 'taskStatus',
      align: 'center'
    },
    {
      title: '托盘状态',
      dataIndex: 'state',
      key: 'state',
      align: 'center'
    },

  ];

  return (
    <Page pageContent={false}>
      <Navbar>
        <NavLeft>
          <a onClick={() => goBack('/wareManage')} className={styles['nav-left']}>
            <img alt="" style={{ height: 24 }} src={backIcon} />
          </a>
        </NavLeft>
        <NavTitle>采购入库单</NavTitle>
        <NavRight className={styles['nav-right']}>
          <Link
            searchbarEnable=".searchbar-demo"
            iconIos="f7:search"
            iconAurora="f7:search"
            iconMd="material:search"
          ></Link>
        </NavRight>
        <Searchbar
          className="searchbar-demo"
          placeholder="请输入采购单号"
          expandable
          searchContainer=".search-list"
          searchIn=".item-title"
          onChange={onChangeSelect}
          onClickClear={onChangeSelect}
          onClickDisable={handleClickDisable}
          disableButtonText="取消"
          style={{ fontSize: 13 }}
        ></Searchbar>
      </Navbar>

      <Toolbar tabbar top noHairline className="ne-top-tab">
        <Link tabLink="#tab-1" onClick={() => setTabKey(0)} tabLinkActive>
          全部
        </Link>
        <Link tabLink="#tab-2" onClick={() => setTabKey(1)}>
          执行中
        </Link>
        <Link tabLink="#tab-3" onClick={() => setTabKey(2)}>
          已完成
        </Link>
      </Toolbar>
      <PageContent
        infinite
        infiniteDistance={50}
        infinitePreloader={showPreloader}
        onInfinite={loadMore}
        ptrPreloader={ptrPreloader}
        ptr
        onPtrRefresh={onHandleRefresh}
        onPtrPullStart={() => {
          setPtrPreloader(true);
        }}
      >
        <div style={{ padding: '0 16px' }} className={styles.tabContainer}>
          <Tabs animated>
            <Tab
              id="tab-1"
              className={`${styles.content} page-content`}
              style={{ paddingTop: '0' }}
              tabActive
            >
              {renderCardList()}
            </Tab>
            <Tab
              id="tab-2"
              className={`${styles.content} page-content`}
              style={{ paddingTop: '0' }}
            >
              {renderCardList()}
            </Tab>
            <Tab
              id="tab-3"
              className={`${styles.content} page-content`}
              style={{ paddingTop: '0' }}
            >
              {renderCardList()}
            </Tab>
          </Tabs>
        </div>
      </PageContent>
      <div className="ne-bottom-container">
        <Button onClick={handleDetail} className="ne-bottom-btn">
          添加采购入库单
        </Button>
      </div>
    </Page>
    // // <Page pageContent={false}>
    //   <PageContent
    //     infinite
    //     infiniteDistance={50}
    //   // infinitePreloader={showPreloader}
    //   // onInfinite={loadMore}
    //   // ptrPreloader={ptrPreloader}
    //   // ptr
    //   // onPtrRefresh={onHandleRefresh}
    //   // onPtrPullStart={() => {
    //   //   setPtrPreloader(true);
    //   // }}
    //   >
    //     <div style={{ padding: '0 16px' }} className={styles.tabContainer}>
    //       <ul className={styles['card-ul']}>
    //         <li>
    //           <span className={styles['li-title']}>{'托盘手动下架'}</span>
    //         </li>
    //         <Card>
    //           {columns.map((column, index) => {
    //             return (
    //               <li key={index}>
    //                 <span className={styles['li-next-title']}>
    //                   {column.title}&nbsp;:&nbsp;&nbsp;&nbsp;
    //                 </span>
    //                 {/* <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span> */}
    //               </li>
    //             );
    //           })}
    //         </Card>
    //       </ul>
    //     </div>
    //   </PageContent>
    // // </Page>
  );
};

export default ManualSheet;
