import React from 'react';
import { Card, CardContent, Button } from '@hvisions/f-ui';
import { i18n, page } from '@hvisions/toolkit';
import styles from './cardInfo.scss';

const getFormattedMsg = i18n.getFormattedMsg;

const CardInfo = ({
  f7router,
  item,
  loadData,
  handleDelete,
  orderLineId,
  state,
  onHandleDelete
}) => {
  
  const columns = [
    {
      title: '收料单号',
      dataIndex: 'receiptNumber',
      key: 'receiptNumber',
      align: 'center',
    },
    {
      title: '行号',
      dataIndex: 'lineNumber',
      key: 'lineNumber',
      align: 'center',
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      key: 'createTime',
      align: 'center',
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center',
    },
    {
      title: '关联单号',
      dataIndex: 'associatedNumber',
      key: 'associatedNumber',
      align: 'center',
    },
    {
      title: '单据数量',
      dataIndex: 'number',
      key: 'number',
      align: 'center',
    },
    {
      title: '单据物料编码',
      dataIndex: 'materialCode',
      key: 'materialCode',
      align: 'center',
    },
    {
      title: '',
      dataIndex: 'materialName',
      key: 'materialName',
      align: 'center',
    },
    {
      title: getFormattedMsg('RawMaterialWarehousing.title.trayNumber'),
      dataIndex: 'trayNumber',
      key: 'trayNumber',
      align: 'center',
    },
    {
      title: getFormattedMsg('RawMaterialWarehousing.title.specifications'),
      dataIndex: 'specifications',
      key: 'specifications',
      align: 'center',
    },
    {
      title: getFormattedMsg('RawMaterialWarehousing.title.actualWeight'),
      dataIndex: 'actualWeight',
      key: 'actualWeight',
      align: 'center',
    },
    {
      title: getFormattedMsg('RawMaterialWarehousing.title.actualNumber'),
      dataIndex: 'actualNumber',
      key: 'actualNumber',
      align: 'center',
    },
    {
      title: getFormattedMsg('RawMaterialWarehousing.title.realityMaterialCode'),
      dataIndex: 'realityMaterialCode',
      key: 'realityMaterialCode',
      align: 'center',
    },
    {
      title: getFormattedMsg('RawMaterialWarehousing.title.realityMaterialName'),
      dataIndex: 'realityMaterialName',
      key: 'realityMaterialName',
      align: 'center',
    }
  ];

  return (
    <div className={styles['card-box']}>
      <Card>
        <ul className={styles['card-ul']}>
          <li>
            <span className={styles['li-title']}>{item.materialName || ''}</span>
          </li>
          {columns.map((column, index) => {
            return (
              <li key={index}>
                <span className={styles['li-next-title']}>{column.title}</span>
                <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span>
              </li>
            );
          })}

          <li className={styles['li-button']}>
            <Button
              fill
              round
              className={styles['delete-button']}
              onClick={() => onHandleDelete(item)}
            >
              删除
            </Button>
          </li>
        </ul>
      </Card>
    </div>
  );
};

export default CardInfo;
