import React, { useEffect, useRef, useState } from 'react';
import { Link, Page, Navbar, NavLeft, NavTitle, NavRight, ListItem, AccordionContent, Fab , List, Icon, PageContent, ListInput } from '@hvisions/f-ui';
import { Sheet, f7, } from 'framework7-react';
import styles from './style.scss';
import backIcon from '~/pages/WarehousinManage/img/backIcon.png';
import { i18n, } from '@hvisions/toolkit';
import { onToast, createDialog } from '~/util/home';
import useDebounce from '~/Hook/useDebounce';
import RawMaterialWarehousingApi from '~/api/RawMaterialWarehousing';
import EmptyPalletDeliveryApi from '~/api/EmptyPalletDelivery';
import { isEmpty } from 'lodash';
import CardInfo from './CardInfo';
import { Skeleton, Empty } from '~/components';

import PrepareAreaServices from '~/api/PrepareArea';
import { PrepareAreaState } from '~/enum/enum';

import bendingMachineServices from '~/api/bendingMachine';



import SemiFinishedDeliveryServices from '~/api/SemiFinishedDelivery';
import CallTraySheet from './CallTray/callTraySheet';
const PickTray = ({ f7router, item, attribute, attributeOne, attributeTwo }) => {
  const countRef = useRef(10);
  const [loading, setLoading] = useState(false);
  const [list, setList] = useState([]);
  const [total, setTotal] = useState(0);
  const [selectValue, setSelectValue] = useState('');
  const debounceSelectValue = useDebounce(selectValue, 500);
  const [showPreloader, setShowPreloader] = useState(false);
  const [allowInfinite, setAllowInfinite] = useState(true);
  const [ptrPreloader, setPtrPreloader] = useState(false);

  const [bindingSheetOpen, setBindingSheetOpen] = useState(false);
  const [trayNumber, setTrayNumber] = useState('');

  const [updateSheetOpen, setUpdateSheetOpen] = useState(false);
  const [areaState, setAreaState] = useState(1);
  const [areaCode, setAreaCode] = useState();





  const [searchValue, setSearchValue] = useState({ attributeOne: attribute, attributeTwo: attributeTwo });
  const [callSheetOpen, setCallSheetOpen] = useState(false);
  const [selectedData, setSelectedData] = useState({});

  useEffect(() => {
    const load = async () => {
      await loadData(debounceSelectValue);
    };
    load();
  }, [debounceSelectValue]);


  useEffect(() => {

  }, [trayNumber, areaState]);

  const loadData = async keyWord => {
    setLoading(true);
    const searchData = {
      // attributeOne: item.attributeOne.toString(),
      // attributeTwo: item.attributeTwo ,
      ...searchValue,
      pageSize: countRef.current,
    };
    await SemiFinishedDeliveryServices
      .getByQuery(searchData)
      .then(res => {
        setList(res.content);
        setTotal(res.totalElements);
      })
      .catch(err => {
        onToast(err.message, styles.toastError);
      });
    setLoading(false);
  };

  const loadMore = async () => {
    if (!allowInfinite) return;
    setShowPreloader(true);
    if (countRef.current >= total) {
      setShowPreloader(false);
      return;
    }
    setAllowInfinite(false);
    countRef.current = countRef.current + 10;
    await loadData(selectValue);
    await setAllowInfinite(true);
  };

  // const onChangeSelect = e => {
  //   if (!e.target.value) {
  //     setSelectValue('');
  //   } else {
  //     setSelectValue(e.target.value);
  //   }
  // };

  // const handleClickDisable = () => {
  //   setSelectValue('');
  // };

  const onHandleRefresh = async done => {
    await setPtrPreloader(true);
    await loadData(selectValue);
    await setPtrPreloader(false);
    await done();
  };

  const renderCardList = () =>
    !loading ? (
      !isEmpty(list) ? (
        list.map(value => (
          <CardInfo
            f7router={f7router}
            key={value.id}
            item={value}
            loadData={loadData}
            selectValue={selectValue}
            modifyData={item}
            setCallSheetOpen={setCallSheetOpen}
            setSelectedData={setSelectedData}
          />
        ))
      ) : (
        <Empty />
      )
    ) : (
      <Skeleton />
    );

      const outSheetClosed = () => {
    setCallSheetOpen(false)
  }

  const style = {
    height: '30px',
    fontSize: '25px',
    fontWeight: 500
  }

  return (
    <Page pageContent={false}>
      <Navbar>
        <NavLeft>
          <a onClick={() => f7router.back()} className="ne-navleft">
            {/* <a onClick={() => handleGoBack()} className="ne-navleft"> */}
            <img alt="" style={{ height: 24 }} src={backIcon} />
          </a>
        </NavLeft>
        <NavTitle>托盘拣选</NavTitle>
        <NavRight className={styles['nav-right']}>
          {/* <Link
            searchbarEnable=".searchbar-demo"
            iconIos="f7:search"
            iconAurora="f7:search"
            iconMd="material:search"
          ></Link> */}
        </NavRight>
      </Navbar>
      <PageContent
        infinite
        infiniteDistance={50}
        infinitePreloader={showPreloader}
        onInfinite={loadMore}
        ptrPreloader={ptrPreloader}
        ptr
        onPtrRefresh={onHandleRefresh}
        onPtrPullStart={() => {
          setPtrPreloader(true);
        }}
      >
        <div style={{ padding: '0 16px' }} className={styles.tabContainer}>
          <div
            className={`${styles.content} page-content`}
            style={{ paddingTop: '0' }}
          >
            {renderCardList()}
          </div>
        </div>
      </PageContent>
      <Sheet
        className={styles['add-sheet']}
        opened={callSheetOpen}
        onSheetClosed={outSheetClosed}
        backdrop
      >
        <CallTraySheet
          outSheetClosed={outSheetClosed}
          item={selectedData}
          loadData={loadData}
          selectValue={selectValue}
          modifyData={item}
        />
      </Sheet>

    <Fab position="right-top" morphTo=".demo-fab-sheet.fab-morph-target">
      <Icon ios="f7:plus" md="material:add" />
    </Fab>
    <div 
    className="list links-list demo-fab-sheet fab-morph-target" 
    slot="fixed"
    style={{margin:'44px 16px 0 16px'}}
    >
      <ul>
      <ListInput outline label="Name" floatingLabel type="text" placeholder="Your name" clearButton>
        <Icon icon="demo-list-icon" slot="media" />
      </ListInput>
      <ListInput outline label="Name" floatingLabel type="text" placeholder="Your name" clearButton>
        <Icon icon="demo-list-icon" slot="media" />
      </ListInput>
      <ListItem accordionItem header='折弯属性' title={<div style={style}>{attributeOne.toString()}</div>} >
              <AccordionContent>
                <List strongIos outlineIos dividersIos >
                  <ListItem checkbox title="大" name="attributeOne-checkbox" value="大" onChange={onAttributeOneChange} />
                  <ListItem checkbox title="中" name="attributeOne-checkbox" value="中" onChange={onAttributeOneChange} />
                  <ListItem checkbox title="小" name="attributeOne-checkbox" value="小" onChange={onAttributeOneChange} />
                </List>
              </AccordionContent>
            </ListItem>

            <ListItem accordionItem header='完工属性' title={<div style={style}>{attributeTwo}</div>} >
              <AccordionContent>
                <List strongIos outlineIos dividersIos >
                  <ListItem checkbox title="切割完工" name="attributeTwo-checkbox" value="切割完工" checked={attributeTwo == "切割完工"} onChange={onAttributeTwoChange} />
                  <ListItem checkbox title="切割未完工" name="attributeTwo-checkbox" value="切割未完工" checked={attributeTwo == "切割未完工"} onChange={onAttributeTwoChange} />
                </List>
              </AccordionContent>
            </ListItem>
      <ListInput outline label="Name" floatingLabel type="text" placeholder="Your name" clearButton>
        <Icon icon="demo-list-icon" slot="media" />
      </ListInput>
      <ListInput outline label="Name" floatingLabel type="text" placeholder="Your name" clearButton>
        <Icon icon="demo-list-icon" slot="media" />
      </ListInput>
        {/* <li>
          <a className="fab-close">Link 2</a>
        </li>
        <li>
          <a className="fab-close">Link 3</a>
        </li>
        <li>
          <a className="fab-close">Link 4</a>
        </li> */}
      </ul>
    </div>

    </Page>
  );
};

export default PickTray;
