import React from 'react';
import { Card, Button } from '@hvisions/f-ui';
import styles from './cardInfo.scss';
import { onToast, createDialog } from '~/util/home';
import EmptyPalletDeliveryApi from '~/api/EmptyPalletDelivery';

const CardInfo = ({ item, loadData, f7router }) => {

  const columns = [
    {
      title: '切割机',
      dataIndex: 'cuttingMachine',
      key: 'cuttingMachine',
      align: 'center',
    }, {
      title: '材料名称',
      dataIndex: 'materialName',
      key: 'materialName',
      align: 'center',
    }, {
      title: '材料编码',
      dataIndex: 'materialCode',
      key: 'materialCode',
      align: 'center',
    }, {
      title: '材料大小 X',
      dataIndex: 'materialSizeX',
      key: 'materialSizeX',
      align: 'center',
    }, {
      title: '材料大小 Y',
      dataIndex: 'materialSizeY',
      key: 'materialSizeY',
      align: 'center',
    }, {
      title: '材料厚度',
      dataIndex: 'materialThickness',
      key: 'materialThickness',
      align: 'center',
    }, {
      title: '计划名称',
      dataIndex: 'planName',
      key: 'planName',
      align: 'center',
    }, {
      title: '出库总数量',
      dataIndex: 'quantity',
      key: 'quantity',
      align: 'center',
    }, {
      title: '剩余数量',
      dataIndex: 'remainderNum',
      key: 'remainderNum',
      align: 'center',
    }, {
    //   title: '任务编码',
    //   dataIndex: 'taskCode',
    //   key: 'taskCode',
    //   align: 'center',
    // }, {
      title: '托盘交接位',
      dataIndex: 'fromLocation',
      key: 'fromLocation',
      align: 'center',
    }, {
      title: '托盘到达位',
      dataIndex: 'toLocation',
      key: 'toLocation',
      align: 'center',
    }, {
      title: '托盘位置',
      dataIndex: 'trayLocation',
      key: 'trayLocation',
      align: 'center',
    }, {
      title: '托盘号',
      dataIndex: 'trayNumber',
      key: 'trayNumber',
      align: 'center',
    }, {
      title: '使用数量',
      dataIndex: 'useNum',
      key: 'useNum',
      align: 'center',
    }, {
      title: '使用状态',
      dataIndex: 'useState',
      key: 'useState',
      align: 'center',
    }
  ]

  const handleSaveManual = () => {
    createDialog('托盘手动下架', '确认开始托盘下架流程？', function() {
      try {
        Manual();
        // onToast('托盘自动下架成功', styles.toastSuccess);
      } catch (error) {
        console.log('error', error);
        onToast('托盘下架失败', styles.toastError);
      }
    });
  };

  const Manual = async () => {
    //托盘出库   托盘下架  新增
    const data = {
      trayNumber: item.code,
      inType: 6, //原料托盘出库
      state: 0,
      destination: '原材料组托点',
      middle: 'J001'
    };
    await EmptyPalletDeliveryApi.saveOrUpdate(data)
      .then(res => {
        onToast('托盘自动下架成功', styles.toastSuccess);
        f7router.back();
        loadData();
      })
      .catch(err => {
        onToast(err.message, styles.toastError);
      });
  };

  const DetailButtons =()=>{
    if (item.remainderNum == 0) {
      return (
        <a key="detail" onClick={() => handleEmpty(item)}>
          空托回库
        </a>
      )
    }
    if (item.remainderNum != 0) {
      return (
        <a key="detail" onClick={() => handleSurplus(item)}>
          余料回库
        </a>
      )
    }
  }

  return (
    <Card className={styles['card']}>
      <ul className={styles['card-ul']}>
      <ul className={styles['card-ul']}>
          <li>
            <span className={styles['li-title']}>{item.taskCode || ''}</span>
          </li>
          {columns.map((column, index) => {
            return (
              <li key={index}>
                <span className={styles['li-next-title']}>{column.title}</span>
                <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span>
              </li>
            );
          })}
        </ul>
      </ul>
      <div className={styles['card-div']}>
        {DetailButtons()}
      </div>
    </Card>
  );
};

export default CardInfo;
