      state: '666'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    },
    {
      code: '444',
      taskStatus: '555',
      state: '666'
    }
  ];

  const loadData = async keyWord => {
    setLoading(true);
    const searchData = {
      receiptNumber: keyWord,
      pageSize: countRef.current,
      state: tabKey,
    };
    await RawMaterialWarehousingApi
      .getByQuery(searchData)
      .then(res => {
        setList(res.content);
        setTotal(res.totalElements);
      })
      .catch(err => {
        onToast(err.message, styles.toastError);
      });
    setLoading(false);
  };

  const loadMore = async () => {
    if (!allowInfinite) return;
    setShowPreloader(true);
    if (countRef.current >= total) {
      setShowPreloader(false);
      return;
    }
    setAllowInfinite(false);
    countRef.current = countRef.current + 10;
    await loadData(selectValue);
    await setAllowInfinite(true);
  };

  const onHandleRefresh = async done => {
    await setPtrPreloader(true);
    await loadData(selectValue);
    await setPtrPreloader(false);
    await done();
  };

  return (
    <PageContent
      infinite
      infiniteDistance={50}
      infinitePreloader={showPreloader}
      onInfinite={loadMore}
      ptrPreloader={ptrPreloader}
      ptr
      onPtrRefresh={onHandleRefresh}
      onPtrPullStart={() => {
        setPtrPreloader(true);
      }}
    >
      <div
        style={{ padding: '0 16px', backgroundColor: '#f4f5f5' }}
        className={styles.tabContainer}
      >
        <ul className={styles['div-ul']}>
          <li>
            <span className={styles['li-title']}>{'托盘手动下架'}</span>
          </li>
          {data.map((value, index) => {
            return (
              <Card key={index} className={styles['card']}>
                <ul className={styles['card-ul']}>
                  <li>
                    <span className={styles['li-next-title']}>托盘号</span>
                    <span className={styles['li-next-title']}>{value.code || ''}</span>
                  </li>
                  <li>
                    <span className={styles['li-next-title']}>任务状态</span>
                    <span className={styles['li-next-title']}>{value.taskStatus || ''}</span>
                  </li>
                  <li>
                    <span className={styles['li-next-title']}>托盘状态</span>
                    <span className={styles['li-next-title']}>{value.state || ''}</span>
                  </li>
                </ul>
                <div className={styles['card-div']}>
                  <Button
                    fill
                    round
                    className={styles['bottom-btn-confirm']}
                    // onClick={handleConfirm}
                  >
                    下架
                  </Button>
                </div>
              </Card>
            );
          })}
        </ul>
      </div>
    </PageContent>
  );
};

export default ManualSheet;
