import React, { useEffect, useRef, useState, useCallback } from 'react';
import {
  Page, Navbar, NavLeft, NavTitle, PageContent, Button, ListInput, List, Card, Input, ListItem,
  AccordionContent,
} from '@hvisions/f-ui';
import styles from './style.scss';
import backIcon from '~/pages/WarehousinManage/img/backIcon.png';
import { onToast } from '~/util/home';

import PrepareAreaApi from '~/api/PrepareArea';
import SemiFinishedWarehousingApi from '~/api/SemiFinishedWarehousing';
import { isEmpty } from 'lodash';
import { Skeleton, Empty } from '~/components';
import useDebounce from '~/Hook/useDebounce';

const OrderInformations = ({
  f7router,
  item,
  orderInfos,
  tableName,
  bendingNumber,
}) => {
  const countRef = useRef(10);
  const [loading, setLoading] = useState(false);


  const [total, setTotal] = useState(0);
  const [showPreloader, setShowPreloader] = useState(false);
  const [allowInfinite, setAllowInfinite] = useState(true);
  const [ptrPreloader, setPtrPreloader] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const [selectValue, setSelectValue] = useState({});
  const debounceSelectValue = useDebounce(selectValue, 500);

  const [inputValue1, setInputValue1] = useState('');
  const [inputValue2, setInputValue2] = useState('');

  const [list, setList] = useState([]);

  useEffect(() => {
    const load = async () => {
      await loadData(debounceSelectValue);
    };
    load();
  }, [debounceSelectValue]);

  // useEffect(() => {
  //   console.log(list, 'list');
  // }, [list]);


  const loadData = async searchData => {
    setLoading(true);
    await SemiFinishedWarehousingApi
      .getAllOderByQuery(searchData)
      .then(res => {
        setList(res.content);
        setTotal(res.totalElements);
      })
      .catch(err => {
        onToast(err.message, styles.toastError);
      });
    setLoading(false);
  };

  const loadMore = async () => {
    if (!allowInfinite) return;
    setShowPreloader(true);
    if (countRef.current >= total) {
      setShowPreloader(false);
      return;
    }
    setAllowInfinite(false);
    countRef.current = countRef.current + 10;
    await loadData(selectValue);
    await setAllowInfinite(true);
  };

  const onHandleRefresh = async done => {
    await setPtrPreloader(true);
    await loadData(selectValue);
    await setPtrPreloader(false);
    await done();
  };

  const renderCardList = () =>
    !loading ? (
      !isEmpty(list) ? (
        list.map(i => (
          <ListItem
            key={i.id}
            checkbox
            title="Movies"
            name="demo-checkbox"
            // checked={movies.length === 2}
            // indeterminate={movies.length === 1}
            // onChange={(e) => onMoviesChange(e)}
            value={i.orderCode}
          >
            <ul slot="root">
              {i.suborderNumberDetails.map(j => {
                <ListItem
                  checkbox
                  title="Movie 1"
                  name="demo-checkbox"
                  // checked={movies.indexOf('Movie 1') >= 0}
                  // onChange={(e) => onMovieChange(e)}
                  value={j.suborderNumber}
                />
              })}
            </ul>
          </ListItem>
        ))
      ) : (
        <Empty />
      )
    ) : (
      <Skeleton />
    );

  const handleGoBack = () => {
    f7router.navigate('/bending-machine-surplusForm', {
      transition: 'ne-backward',
      props: {
        item: item,
        orderInfos: orderInfos,
        tableName,
        bendingNumber,
        orderNumberV: inputValue1,
        suborderNumberV: inputValue2,
      }
    });
  };

  const onSearch = async () => {
    console.log('inputValue', inputValue1, inputValue2);
    const params = {}
    inputValue1 == '' ? delete params.orderNumber : params.orderNumber = inputValue1
    inputValue2 == '' ? delete params.suborderNumber : params.suborderNumber = inputValue2

    await SemiFinishedWarehousingApi.getAllOderByQuery(params)
      .then(res => {
        setList(res)
      })
      .catch(err => {
        onToast(err.message, styles.toastError);
      });
    setSelectValue(params)
  }



  const [movies, setMovies] = useState(['Movie 1']);
  const onMovieChange = (e) => {
    const value = e.target.value;
    if (e.target.checked) {
      movies.push(value);
    } else {
      movies.splice(movies.indexOf(value), 1);
    }
    setMovies([...movies]);
  };

  const onMoviesChange = () => {
    if (movies.length === 1 || movies.length === 0) {
      setMovies(['Movie 1', 'Movie 2']);
    } else if (movies.length === 2) {
      setMovies([]);
    }
  };

  return (
    <Page pageContent={false}>
      <Navbar>
        <NavLeft>
          {/* <a onClick={() => f7router.back()} className="ne-navleft"> */}
          <a onClick={() => handleGoBack()} className="ne-navleft">
            <img alt="" style={{ height: 24 }} src={backIcon} />
          </a>
        </NavLeft>
        <NavTitle>添加订单信息</NavTitle>
      </Navbar>
      <PageContent
        infinite
        infiniteDistance={50}
        infinitePreloader={showPreloader}
        ptrPreloader={ptrPreloader}
        ptr
        onPtrPullStart={() => {
          setPtrPreloader(true);
        }}
      >
        <div style={{ padding: '0 16px 56px 16px' }} className={styles.tabContainer}>
          <Card className={styles['card-box']}>
            <div className={styles['search-box']}>
              <div >
                <input
                  type="text"
                  placeholder="请输入主订单号"
                  onChange={e => setInputValue1(e.target.value)}
                  className={styles['input-style']}
                  style={{ marginBottom: '5px' }}
                  value={inputValue1}
                />
                <input
                  type="text"
                  placeholder="请输入子订单号"
                  onChange={e => setInputValue2(e.target.value)}
                  className={styles['input-style']}
                  style={{ marginTop: '5px' }}
                  value={inputValue2}
                />
              </div>
              <Button
                onClick={onSearch}
                className={styles['search-btn']}
              >
                确认
              </Button>
            </div>
          </Card>
          {/* <List strongIos outlineIos dividersIos className={styles['list-div']}>
            {renderCardList()}
          </List> */}

          <PageContent
            infinite
            infiniteDistance={50}
            infinitePreloader={showPreloader}
            onInfinite={loadMore}
            ptrPreloader={ptrPreloader}
            ptr
            onPtrRefresh={onHandleRefresh}
            onPtrPullStart={() => {
              setPtrPreloader(true);
            }}
          >
            <div style={{ padding: '0 16px' }} className={styles.tabContainer}>
              <div
                className={`${styles.content} page-content`}
                style={{ paddingTop: '0' }}
              >
                <List strongIos outlineIos dividersIos className={styles['list-div']}>
                  {renderCardList()}
                </List>
              </div>
            </div>
          </PageContent>
          {/* {list.map(i => {
            <List strongIos outlineIos dividersIos className={styles['list-div']} accordionList>
              <ListItem
                checkbox
                title="Movies"
                name="demo-checkbox"
                // checked={movies.length === 2}
                // indeterminate={movies.length === 1}
                // onChange={(e) => onMoviesChange(e)}
                value={i.orderCode}
              >
                <ul slot="root">
                  {i.suborderNumberDetails.map(j => {
                    <ListItem
                      checkbox
                      title="Movie 1"
                      name="demo-checkbox"
                      // checked={movies.indexOf('Movie 1') >= 0}
                      // onChange={(e) => onMovieChange(e)}
                      value={j.suborderNumber}
                    />
                  })}
                </ul>
              </ListItem>
            </List>
          })} */}
        </div>
      </PageContent>
    </Page >
  );
};

export default OrderInformations;
