import React, { useEffect, useState } from 'react';
import { Card, CardContent, Button } from '@hvisions/f-ui';
import { i18n, page } from '@hvisions/toolkit';
import styles from './cardInfo.scss';

const getFormattedMsg = i18n.getFormattedMsg;

const CardInfo = ({
  f7router,
  item,
  loadData,
  handleDelete,
  onHandleDelete
}) => {
  const [style, setStyle] = useState();
  const [state, setState] = useState();

  useEffect(() => {
    if (item.state == 0) { setStyle(newStyle); setState('新建') }
    if (item.state == 1) { setStyle(onGoingStyle); setState('执行中') }
    if (item.state == 2) { setStyle(completeStyle); setState('已完成') }
  }, []);

  const newStyle = {
    background: '#ffdad4',
    color: '#d83333'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };
  const completeStyle = {
    background: '#D3F0E9',
    color: '#42BB9E'
  };

  const columns = [
    // {
    //   title: '收料单号',
    //   dataIndex: 'receiptNumber',
    //   key: 'receiptNumber',
    //   align: 'center',
    // },
    // {
    //   title: '行号',
    //   dataIndex: 'lineNumber',
    //   key: 'lineNumber',
    //   align: 'center',
    // },

    {
      title: '关联单号',
      dataIndex: 'associatedNumber',
      key: 'associatedNumber',
      align: 'center',
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      key: 'createTime',
      align: 'center',
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center',
    },
    // {
    //   title: '单据数量',
    //   dataIndex: 'number',
    //   key: 'number',
    //   align: 'center',
    // },
    // {
    //   title: '单据物料编码',
    //   dataIndex: 'materialCode',
    //   key: 'materialCode',
    //   align: 'center',
    // },
    // {
    //   title: '单据物料名称',
    //   dataIndex: 'materialName',
    //   key: 'materialName',
    //   align: 'center',
    // },
    // {
    //   title: '托盘号',
    //   dataIndex: 'trayNumber',
    //   key: 'trayNumber',
    //   align: 'center',
    // },
    // {
    //   title: '规格',
    //   dataIndex: 'specifications',
    //   key: 'specifications',
    //   align: 'center',
    // },
    // {
    //   title: '实际重量',
    //   dataIndex: 'actualWeight',
    //   key: 'actualWeight',
    //   align: 'center',
    // },
    // {
    //   title: '实际数量',
    //   dataIndex: 'actualNumber',
    //   key: 'actualNumber',
    //   align: 'center',
    // },
    // {
    //   title: '实际物料编码',
    //   dataIndex: 'realityMaterialCode',
    //   key: 'realityMaterialCode',
    //   align: 'center',
    // },
    // {
    //   title: '实际物料名称',
    //   dataIndex: 'realityMaterialName',
    //   key: 'realityMaterialName',
    //   align: 'center',
    // }
  ];

  const onHandleClick = () => {
    f7router.navigate('/inStorage-operate', {
      props: { purchaseNo: item.receiptNumber, orderLineId: item.id, state: item.state }
    });
  };

  return (
    <div className={styles['card-box']} onClick={onHandleClick}>
      <Card>
        <ul className={styles['card-ul']}>
          <li>
            <span className={styles['li-title']}>{item.receiptNumber || ''}</span>
            <span
              className={styles['li-status']}
              style={style}
            >
              {state}
            </span>
          </li>
          {columns.map((column, index) => {
            return (
              <li key={index}>
                <span className={styles['li-next-title']}>{column.title}</span>
                <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span>
              </li>
            );
          })}
        </ul>
      </Card>
    </div>
  );
};

export default CardInfo;
