import React, { useEffect, useRef, useState } from 'react';
import { BlockTitle, ListInput, List, Icon, Button } from '@hvisions/f-ui';
import { attributeOne, attributeTwo, dockingPoints, sortPositions } from '~/enum/enum';
import styles from './style.scss';
import RawMaterialWarehousingApi from '~/api/RawMaterialWarehousing';

const Manual = ({ f7router}) => {

  const [materialList, setMaterialList] = useState([])

  useEffect(() => {
    getMaterial()
  }, [])

  const getMaterial = async (keyWord = '') => {
    const params = {
      keyWord,
      pageSize: 20,
      page: 0,
    }
    await RawMaterialWarehousingApi.getMaterialByNameOrCode(params)
      .then(res => {
        const data = res.content
        data.map(item => {
          const extend = item.extend
          item.length = 'length' in extend ? extend.length : '   ',
            item.width = 'width' in extend ? extend.width : '   ',
            item.thickness = 'thickness' in extend ? extend.thickness : '   ',
            item.thickness = 'materialType' in extend ? extend.materialType : '   '
        })
        console.log(data, 'materialList');
        setMaterialList(data)
      })
  }

  return (
    <>
        <BlockTitle>手动出库</BlockTitle>
        <List strongIos dividersIos insetIos style={{ padding: '0 16px' }}>
            <ListInput
                label="托盘号"
                type="text"
                placeholder="请输入托盘号"
                required
                validate
                // onChange={(e) => {
                //     setSortPosition(e.target.value)
                // }}
                // value={sortPosition}
            >
                <Icon icon="demo-list-icon" slot="media" />
            </ListInput>
            <ListInput
                label="物料"
                type="select"
                placeholder="请选择物料"
                required
                validate
                // onChange={(e) => {
                //     setDockingPoint(e.target.value)
                // }}
                // value={dockingPoint}
            >
                <Icon icon="demo-list-icon" slot="media" />
                {materialList.length && materialList.map((value, index) => (
                    <option value={value.id} key={value.id}>
                        {value.materialName} -- {value.materialCode} -- {value.materialType} -- {value.length} -- {value.width} -- {value.thickness}
                    </option>
                ))}
            </ListInput>
            <ListInput
                label="物料批号"
                type="text"
                placeholder="请输入物料批号"
                required
                validate
                // onChange={(e) => {
                //     setSortPosition(e.target.value)
                // }}
                // value={sortPosition}
            >
                <Icon icon="demo-list-icon" slot="media" />
            </ListInput>
            <ListInput
                label="上料口"
                type="text"
                placeholder="请输入上料口"
                required
                validate
                // onChange={(e) => {
                //     setSortPosition(e.target.value)
                // }}
                // value={sortPosition}
            >
                <Icon icon="demo-list-icon" slot="media" />
            </ListInput>
            <ListInput
                label="中间位置"
                type="text"
                placeholder="请输入中间位置"
                required
                validate
                // onChange={(e) => {
                //     setSortPosition(e.target.value)
                // }}
                // value={sortPosition}
            >
                <Icon icon="demo-list-icon" slot="media" />
            </ListInput>
            <ListInput
                label="目标位置"
                type="text"
                placeholder="请输入目标位置"
                required
                validate
                // onChange={(e) => {
                //     setSortPosition(e.target.value)
                // }}
                // value={sortPosition}
            >
                <Icon icon="demo-list-icon" slot="media" />
            </ListInput>
            <ListInput
                label="物料数量"
                type="text"
                placeholder="请输入物料数量"
                required
                validate
                // onChange={(e) => {
                //     setSortPosition(e.target.value)
                // }}
                // value={sortPosition}
            >
                <Icon icon="demo-list-icon" slot="media" />
            </ListInput>

            {/* <Button className={styles['save-btn']} fill round onClick={callTraySave}>
                保存
            </Button> */}
        </List>
    </>
)
};

export default Manual;
