    {
      title: '托盘号',
      dataIndex: 'code',
      key: 'code',
      align: 'center'
    },
    {
      title: '任务状态',
      dataIndex: 'taskStatus',
      key: 'taskStatus',
      align: 'center'
    },
    {
      title: '托盘状态',
      dataIndex: 'state',
      key: 'state',
      align: 'center'
    },

  ];

  return (
    <Page pageContent={false}>
      <PageContent
        infinite
        infiniteDistance={50}
        // infinitePreloader={showPreloader}
