import React, { useEffect, useState } from 'react';
import { Card, Button, Checkbox ,Sheet} from '@hvisions/f-ui';
import styles from './CardInfo.scss';
import PrepareAreaServices from '~/api/PrepareArea';
import { onToast, createDialog } from '~/util/home';
import CallTraySheet from './CallTray/callTraySheet';
import SemiFinishedDeliveryServices from '~/api/SemiFinishedDelivery';

const CardInfo = ({
  f7router,
  item,
  // setBindingSheetOpen,
  // setUpdateSheetOpen,
  // setAreaState,
  // setAreaCode,
  // selectValue,
  // loadData,
}) => {

  const [style, setStyle] = useState();
  const [state, setState] = useState();
  const [callSheetOpen, setCallSheetOpen] = useState(false);
  const [readyMaterials, setReadyMaterials] = useState();
  const [dockingPoint, setDockingPoint] = useState();

  useEffect(() => {
    console.log(item);
    if (item.state == 1) {
      setStyle(newStyle);
      setState('未占用');
    }
    if (item.state == 2) {
      setStyle(onGoingStyle);
      setState('已占用');
    }
  }, []);

  const newStyle = {
    background: '#ffdad4',
    color: '#d83333'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };
  const completeStyle = {
    background: '#D3F0E9',
    color: '#42BB9E'
  };

  // const CheckboxChange = async (e, record) => {
  //   const automaticState = e.target.checked
  //   const data = record
  //   data.automaticState = automaticState
  //   await PrepareAreaServices.updatePrepareArea(data)
  //     .then(res => {
  //       onToast('修改成功', styles.toastSuccess);
  //       loadData(selectValue);
  //     })
  //     .catch(err => {
  //       onToast(err.message, styles.toastError);
  //     })
  // }

  const handleSaveOut = () => {
    setCallSheetOpen(true)
  }

  const outSheetClosed = () => {
    setCallSheetOpen(false)
    setReadyMaterials();
    setDockingPoint();
  }

  return (
    <div className={styles['card-box']}>
      <Card className={styles['card']}>
        <ul className={styles['card-ul']}>
          <li>
            <span className={styles['li-title']}>{item.trayNumber || ''}</span>
            <span
              className={styles['li-status']}
              style={style}
            >
              {state}
            </span>
          </li>
          <li>
            <span className={styles['li-next-title']}>折弯机名称</span>
            <span className={styles['li-next-title']}>{item.locationNumber || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>折弯机属性</span>
            <span className={styles['li-next-title']}>{item.orderNumber || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>是否允许切割未完成出库</span>
            <span className={styles['li-next-title']}>{item.suborderNumber || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>最小在库时间</span>
            <span className={styles['li-next-title']}>{item.productName}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>备料区</span>
            <span className={styles['li-next-title']}>{item.productCode || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>托盘号</span>
            <span className={styles['li-next-title']}>{item.productNum || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>折弯机名称</span>
            <span className={styles['li-next-title']}>{item.attributeOne || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>折弯机属性</span>
            <span className={styles['li-next-title']}>{item.attributeTwo || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>是否允许切割未完成出库</span>
            <span className={styles['li-next-title']}>{item.intime || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>备料区</span>
            <span className={styles['li-next-title']}>{item.description || ''}</span>
          </li>
        </ul>
        <div className={styles['card-div']}>
          <ul className={styles['div-ul']}>
            <Button fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }} onClick={() => handleSaveOut()}>
              出库
            </Button>
          </ul>
        </div>
      </Card>
      <Sheet
        className={styles['add-sheet']}
        opened={callSheetOpen}
        onSheetClosed={outSheetClosed}
        backdrop
      >
        <CallTraySheet
          outSheetClosed={outSheetClosed}
          item={item}
        />
      </Sheet>
    </div>
  );
};

export default CardInfo;
