import React, { useEffect, useRef, useState } from 'react';
import { Toolbar, Link, Tabs, Tab, Page, Navbar, NavLeft, NavTitle, NavRight, ListItem, AccordionContent, Fab, Searchbar, BlockTitle, ListInput, List, Icon, PageContent, Button, Input, Form } from '@hvisions/f-ui';
import { Sheet, f7, } from 'framework7-react';
import styles from './style.scss';
import backIcon from '~/pages/WarehousinManage/img/backIcon.png';
import { i18n, } from '@hvisions/toolkit';
import { onToast, createDialog } from '~/util/home';
import useDebounce from '~/Hook/useDebounce';
import RawMaterialWarehousingApi from '~/api/RawMaterialWarehousing';
import EmptyPalletDeliveryApi from '~/api/EmptyPalletDelivery';
import { isEmpty } from 'lodash';
import CardInfo from './cardInfo';
import { Skeleton, Empty } from '~/components';
import TaskOverviewApi from '~/api/TaskOverview';
import { taskType, taskState, agvState, transportState } from '~/enum/enum';

const getFormattedMsg = i18n.getFormattedMsg;

const TaskOverview = ({ f7router }) => {
  const [tabKey, setTabKey] = useState(1);

  const countRef = useRef(10);
  const [loading, setLoading] = useState(false);
  const [list, setList] = useState([]);
  const [total, setTotal] = useState(0);
  // const [selectValue, setSelectValue] = useState('');
  const [selectValue, setSelectValue] = useState({});
  const debounceSelectValue = useDebounce(selectValue, 500);
  const [showPreloader, setShowPreloader] = useState(false);
  const [allowInfinite, setAllowInfinite] = useState(true);
  const [ptrPreloader, setPtrPreloader] = useState(false);

  const [adjustSheetOpen, setAdjustSheetOpen] = useState(false);
  const [adjustSheetData, setAdjustSheetData] = useState({});
  const [priority, setPriority] = useState('');

  useEffect(() => {
    const load = async () => {
      await loadData(debounceSelectValue);
    };
    load();
  }, [tabKey, debounceSelectValue]);

  const loadData = async data => {
    setLoading(true);
    const searchData = {
      ...data,
      pageSize: countRef.current,
      taskState: tabKey,
    };
    await TaskOverviewApi
      .findTaskView(searchData)
      .then(res => {
        const data = res.content
        data.map(i => {
          i.taskType == null ? '' :
            i.taskTypeName = taskType[i.taskType - 1].name
          i.agvState == null ? '' :
            i.agvStateName = agvState[i.agvState - 1].name
          i.transportState == null ? '' :
            i.transportStateName = transportState[i.transportState - 1].name
        })
        setList(data);
        setTotal(res.totalElements);
      })
      .catch(err => {
        onToast(err.message, styles.toastError);
      });
    setLoading(false);
  };

  const loadMore = async () => {
    if (!allowInfinite) return;
    setShowPreloader(true);
    if (countRef.current >= total) {
      setShowPreloader(false);
      return;
    }
    setAllowInfinite(false);
    countRef.current = countRef.current + 10;
    await loadData(selectValue);
    await setAllowInfinite(true);
  };

  const onChangeSelect = e => {
    if (!e.target.value) {
      setSelectValue('');
    } else {
      setSelectValue(e.target.value);
    }
  };

  const handleClickDisable = () => {
    setSelectValue('');
  };

  const onHandleRefresh = async done => {
    await setPtrPreloader(true);
    await loadData(selectValue);
    await setPtrPreloader(false);
    await done();
  };

  const renderCardList = () =>
    !loading ? (
      !isEmpty(list) ? (
        list.map(value => (
          <CardInfo
            key={value.id}
            item={value}
            tabKey={tabKey}
            setAdjustSheetOpen={setAdjustSheetOpen}
            setAdjustSheetData={setAdjustSheetData}
            selectValue={selectValue}
            loadData={loadData}
          />
        ))
      ) : (
        <Empty />
      )
    ) : (
      <Skeleton />
    );

  const adjustSheetClosed = () => {
    setAdjustSheetOpen(false)
    setAdjustSheetData({})
    setPriority('')
  }

  const handleSaveAdjust = async () => {
    await TaskOverviewApi.adjustPriority(adjustSheetData.id, priority)
      .then(res => {
        onToast('任务优先级调整成功', styles.toastSuccess);
        adjustSheetClosed()
        loadData(selectValue);
      })
      .catch(err => {
        onToast(err.message, styles.toastError);
      })
  }

  const style = {
    // height: '30px',
    // fontSize: '25px',
    fontWeight: 500
  }

  const [taskCode, setTaskCode] = useState('');
  const [type, setType] = useState();
  const [oderCode, setOderCode] = useState('');

  const onSearchValueChange = (value, checked, keyValue) => {
    if (keyValue == 'taskCode') {
      setTaskCode(value)
      return
    }
    if (keyValue == 'type') {
      setType(value)
      return
    }
    if (keyValue == 'oderCode') {
      setOderCode(value)
      return
    }
  }

  const onSearch = () => {
    const data = {}
    taskCode != '' ? data.taskCode = taskCode : 1
    taskType != '' ? data.type = type : 1
    oderCode != '' ? data.oderCode = oderCode : 1
    console.log(data, "data");
    setSelectValue(data)
  }

  return (
    <Page pageContent={false}>
      <Navbar>
        <NavLeft>
          <a onClick={() => f7router.back('/')} className="ne-navleft">
            <img alt="" style={{ height: 24 }} src={backIcon} />
          </a>
        </NavLeft>
        <NavTitle>任务总览</NavTitle>
        {/* <NavRight className={styles['nav-right']}>
          <Link
            searchbarEnable=".searchbar-demo"
            iconIos="f7:search"
            iconAurora="f7:search"
            iconMd="material:search"
          ></Link>
        </NavRight>
        <Searchbar
          className="searchbar-demo"
          placeholder="请输入任务编码"
          expandable
          searchContainer=".search-list"
          searchIn=".item-title"
          onChange={onChangeSelect}
          onClickClear={onChangeSelect}
          onClickDisable={handleClickDisable}
          disableButtonText="取消"
          style={{ fontSize: 13 }}
        ></Searchbar> */}
      </Navbar>
      <Toolbar tabbar top noHairline className="ne-top-tab">
        <Link tabLink="#tab-1" onClick={() => setTabKey(1)} tabLinkActive={tabKey == 1}>
          排队中
        </Link>
        <Link tabLink="#tab-2" onClick={() => setTabKey(2)} tabLinkActive={tabKey == 2}>
          执行中
        </Link>
        <Link tabLink="#tab-3" onClick={() => setTabKey(3)} tabLinkActive={tabKey == 3}>
          暂停
        </Link>
        <Link tabLink="#tab-4" onClick={() => setTabKey(4)} tabLinkActive={tabKey == 4}>
          完成
        </Link>
        {/* <Link tabLink="#tab-5" onClick={() => setTabKey(5)} tabLinkActive ={tabKey == 5 }>
          异常
        </Link> */}
      </Toolbar>
      <PageContent
        infinite
        infiniteDistance={50}
        infinitePreloader={showPreloader}
        onInfinite={loadMore}
        ptrPreloader={ptrPreloader}
        ptr
        onPtrRefresh={onHandleRefresh}
        onPtrPullStart={() => {
          setPtrPreloader(true);
        }}
      >
        <div style={{ padding: '0 16px' }} className={styles.tabContainer}>
          <Tabs animated>
            <Tab
              id="tab-1"
              className={`${styles.content} page-content`}
              style={{ paddingTop: '0' }}
              tabActive
            >
              {renderCardList()}
            </Tab>
            <Tab
              id="tab-2"
              className={`${styles.content} page-content`}
              style={{ paddingTop: '0' }}
            >
              {renderCardList()}
            </Tab>
            <Tab
              id="tab-3"
              className={`${styles.content} page-content`}
              style={{ paddingTop: '0' }}
            >
              {renderCardList()}
            </Tab>
            <Tab
              id="tab-4"
              className={`${styles.content} page-content`}
              style={{ paddingTop: '0' }}
            >
              {renderCardList()}
            </Tab>
            {/* <Tab
              id="tab-5"
              className={`${styles.content} page-content`}
              style={{ paddingTop: '0' }}
            >
              {renderCardList()}
            </Tab> */}
          </Tabs>
        </div>
      </PageContent>
      <Sheet
        className={styles['add-sheet']}
        opened={adjustSheetOpen}
        onSheetClosed={adjustSheetClosed}
        backdrop
      >
        <BlockTitle>任务优先级调整</BlockTitle>
        <List strongIos dividersIos insetIos style={{ padding: '0 16px' }}>
          <ListInput
            label="任务编码"
            type="text"
            disabled={true}
            value={adjustSheetData.taskCode}
          >
            <Icon icon="demo-list-icon" slot="media" />
          </ListInput>
          <ListInput
            label="原任务优先级"
            type="text"
            disabled={true}
            value={adjustSheetData.priority}
          >
            <Icon icon="demo-list-icon" slot="media" />
          </ListInput>
          <ListInput
            label="任务优先级"
            type="text"
            placeholder="请输入任务优先级"
            required
            validate
            clearButton
            onChange={(e) => {
              setPriority(e.target.value)
            }}
            value={priority}
          >
            <Icon icon="demo-list-icon" slot="media" />
          </ListInput>

          <Button className={styles['save-btn']} fill round onClick={handleSaveAdjust}>
            确认
          </Button>
        </List>
      </Sheet>
      <Fab position="right-top" morphTo=".demo-fab-sheet.fab-morph-target" style={{ marginTop: 65, width: 45, height: 45 }}>
        <Icon ios="f7:search" md="material:Search" />
      </Fab>
      <div
        className="list  demo-fab-sheet fab-morph-target"
        slot="fixed"
        style={{ margin: '88px 16px 0 16px' }}
      >
        <List accordionList accordionOpposite >
          <ListItem accordionItem title='任务编码' after={<div style={style}>{taskCode || '请输入任务编码'}</div>} >
            <AccordionContent>
              <List>
                <ListInput type="text" placeholder="请输入任务编码" onChange={e => onSearchValueChange(e.target.value, '', 'taskCode')} />
              </List>
            </AccordionContent>
          </ListItem>
          <ListItem accordionItem title='任务类型' after={<div style={style}>{type || '请选择任务类型'}</div>} >
            <AccordionContent>
              <Page pageContent={false}>
                <List strongIos outlineIos dividersIos >
                  {taskType.map(item => {
                    return <ListItem key={item.id} checkbox title={item.value} name="attributeOne-checkbox" value={item.id} checked={type == item.id} onChange={e => onSearchValueChange(e.target.value, e.target.checked, 'type')} />
                  })}
                </List>
              </Page>
            </AccordionContent>
          </ListItem>
          <ListItem accordionItem title='业务号' after={<div style={style}>{oderCode || '请输入业务号'}</div>} >
            <AccordionContent>
              <List>
                <ListInput type="text" placeholder="请输入业务号" onChange={e => onSearchValueChange(e.target.value, '', 'oderCode')} />
              </List>
            </AccordionContent>
          </ListItem>
          <li>
            <Button className="fab-close" large onClick={onSearch}>查询</Button>
          </li>
        </List>
      </div>
    </Page>
  );
};

export default TaskOverview;
