import React from 'react';
import styles from './style.scss';
import { PageContent, List, ListItem ,ListInput, Icon, Button} from 'framework7-react';

const BindingSheet = ({ handleSave,tyayNumber,rawMaterial,createSheetValue }) => {
  const cardColumns = [
    {
      title: '行号',
      dataIndex: 'lineNumber',
      key: 'lineNumber',
      align: 'center'
    },

    {
      title: '关联单号',
      dataIndex: 'associatedNumber',
      key: 'associatedNumber',
      align: 'center'
    },
    {
      title: '创建时间',
      dataIndex: 'createTime',
      key: 'createTime',
      align: 'center'
    },
    {
      title: '创建人',
      dataIndex: 'creator',
      key: 'creator',
      align: 'center'
    },
    {
      title: '单据数量',
      dataIndex: 'number',
      key: 'number',
      align: 'center'
    },
    {
      title: '单据物料编码',
      dataIndex: 'materialCode',
      key: 'materialCode',
      align: 'center'
    },
    {
      title: '单据物料名称',
      dataIndex: 'materialName',
      key: 'materialName',
      align: 'center'
    },
    {
      title: '托盘号',
      dataIndex: 'trayNumber',
      key: 'trayNumber',
      align: 'center'
    },
    {
      title: '规格',
      dataIndex: 'specifications',
      key: 'specifications',
      align: 'center'
    },
    {
      title: '实际重量',
      dataIndex: 'actualWeight',
      key: 'actualWeight',
      align: 'center'
    },
    {
      title: '实际数量',
      dataIndex: 'actualNumber',
      key: 'actualNumber',
      align: 'center'
    },
    {
      title: '实际物料编码',
      dataIndex: 'realityMaterialCode',
      key: 'realityMaterialCode',
      align: 'center'
    },
    {
      title: '实际物料名称',
      dataIndex: 'realityMaterialName',
      key: 'realityMaterialName',
      align: 'center'
    }
  ];

  return (
<List strongIos dividersIos insetIos style={{ padding: '0 16px' }}>
          <ListInput
            label="托盘号"
            type="text"
            placeholder="请输入托盘号"
            required
            validate
            clearButton
            defaultValue={tyayNumber}
            onChange={(e)=>{
              const v = createSheetValue
              v.tyayNumber = e.target.value
              setCreateSheetValue(v)
            }}
          >
            <Icon icon="demo-list-icon" slot="media" />
          </ListInput>
          <ListInput
            label="原料"
            type="text"
            placeholder="请输入原料"
            required
            validate
            clearButton
            defaultValue={rawMaterial}
          >
            <Icon icon="demo-list-icon" slot="media" />
          </ListInput>

            <Button className={styles['save-btn']} fill round onClick={handleSave}>
              保存
            </Button>
        </List>
  );
};

export default BindingSheet;
