import React, { useEffect, useState } from 'react';
import { Card, CardContent, Button ,Checkbox,Sheet ,BlockTitle ,List ,ListInput ,Icon} from '@hvisions/f-ui';

import styles from './CardInfo.scss';
import { getAuthData } from '@hvisions/toolkit/lib/session';
import classnames from 'classnames';
const CardInfo = ({ f7router, item, loadData, handleDelete, handleInStorage }) => {

  const [style, setStyle] = useState();
  const [state, setState] = useState();
  
  const [bindingSheetOpen, setBindingSheetOpen] = useState(false);
  const [trayNumber, setTrayNumber] = useState();

  const [updateSheetOpen, setUpdateSheetOpen] = useState(false);

  useEffect(() => {
    if (item.areaState == 0) {
      setStyle(newStyle);
      setState('空置');
    }
    if (item.areaState == 1) {
      setStyle(onGoingStyle);
      setState('使用中');
    }
    if (item.areaState == 2) {
      setStyle(completeStyle);
      setState('运输中');
    }
  }, []);

  const newStyle = {
    background: '#ffdad4',
    color: '#d83333'
  };
  const onGoingStyle = {
    background: '#E1EDFF',
    color: '#3D86F3'
  };
  const completeStyle = {
    background: '#D3F0E9',
    color: '#42BB9E'
  };

  const bindingSheetClosed =()=>{
    setBindingSheetOpen(false);
    setBindingSheetOpen();
  }

  const handleBindingSave =()=>{

  }

  const updateSheetClosed =()=>{
    setUpdateSheetOpen(false);
  }

  return (
    <div className={styles['card-box']}>
      <Card className={styles['card']}>
        <ul className={styles['card-ul']}>
          <li>
            <span className={styles['li-title']}>{item.areaCode || ''}</span>
            <span
              className={styles['li-status']}
              style={style}
            >
              {state}
            </span>
          </li>
          <li>
            <span className={styles['li-next-title']}>托盘号</span>
            <span className={styles['li-next-title']}>{item.transferCode || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>任务状态</span>
            <span className={styles['li-next-title']}>{item.taskState || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>接驳口</span>
            <span className={styles['li-next-title']}>{item.joinArea || ''}</span>
          </li>
          <li>
            <span className={styles['li-next-title']}>设备名称</span>
            <span className={styles['li-next-title']}>{item.equipmentName || ''}</span>
          </li>
        </ul>
        <div className={styles['card-div']}>
          <ul className={styles['div-ul']}>
            <Button fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }} onClick={()=>{setBindingSheetOpen(true);}}>
              绑定托盘
            </Button>
            <Button fill round className={styles['bottom-btn-confirm']} style={{ margin: "5px" }} onClick={()=>{setUpdateSheetOpen(true);}}>
              更新状态
            </Button>
            <Checkbox name="checkbox-1" style={{ margin: "5px" }} />自动下架托盘
          </ul>
      </div>
      </Card>
      <Sheet
        className={styles['bottom-sheet']}
        opened={bindingSheetOpen}
        onSheetClosed={bindingSheetClosed}
        backdrop
      >
        <BlockTitle>托盘物料绑定</BlockTitle>
        <List strongIos dividersIos insetIos style={{ padding: '0 16px' }}>
          <ListInput
          key={'addSheet'}
            label="托盘号"
            type="text"
            placeholder="请输入托盘号"
            required
            validate
            clearButton
            onChange={(e)=>{
              setTrayNumber(e.target.value)
            }}
            value={trayNumber}
          >
            <Icon icon="demo-list-icon" slot="media" />
          </ListInput>
            <Button className={styles['save-btn']} fill round onClick={handleBindingSave}>
              保存
            </Button>
        </List>
      </Sheet>
      <Sheet
        className={styles['bottom-sheet']}
        opened={updateSheetOpen}
        onSheetClosed={updateSheetClosed}
        backdrop
      >
        <BlockTitle>托盘物料绑定</BlockTitle>
        {/* <List strongIos dividersIos insetIos style={{ padding: '0 16px' }}>
          <ListInput
          key={'addSheet'}
            label="托盘号"
            type="text"
            placeholder="请输入托盘号"
            required
            validate
            clearButton
            onChange={(e)=>{
              const v = createSheetValue
              v.tyayNumber = e.target.value
              setCreateSheetValue(v)
              setTyayNumber(e.target.value)
            }}
            value={tyayNumber}
          >
            <Icon icon="demo-list-icon" slot="media" />
          </ListInput>
          <ListInput
            label="原料"
            type="text"
            placeholder="请输入原料"
            required
            validate
            clearButton
            onChange={(e)=>{
              const v = createSheetValue
              v.rawMaterial = e.target.value
              setCreateSheetValue(v)
              setRawMaterial(e.target.value)
            }}
            value={rawMaterial}
          >
            <Icon icon="demo-list-icon" slot="media" />
          </ListInput>

            <Button className={styles['save-btn']} fill round onClick={handleSave}>
              保存
            </Button>
        </List> */}
      </Sheet>
    </div>
  );
};

export default CardInfo;
