import React, { useEffect, useState } from 'react';
import { Card, Button } from '@hvisions/f-ui';
import styles from './cardInfo.scss';
import { Sheet } from 'framework7-react';
import CardSheet from './CardSheet/index';

const CardInfo = ({ 
  item, 
  setAdjustSheetOpen,
  setAdjustSheetData 
}) => {
  const [style, setStyle] = useState();
  const [state, setState] = useState();
  const [sheetValue, setSheetValue] = useState({});
  const [sheetOpened, setSheetOpened] = useState(false);

  const columns = [
    {
      title: '任务类型',
      dataIndex: 'taskType',
      key: 'taskType',
      align: 'center'
    },
    {
      title: '托盘号',
      dataIndex: 'transferCode',
      key: 'transferCode',
      align: 'center'
    },
    {
      title: '起始位置',
      dataIndex: 'fromLocation',
      key: 'fromLocation',
      align: 'center'
    },
    {
      title: '中间位置',
      dataIndex: 'middle',
      key: 'middle',
      align: 'center'
    },
    {
      title: '目标位置',
      dataIndex: 'toLocation',
      key: 'toLocation',
      align: 'center'
    },
    {
      title: '优先级',
      dataIndex: 'priority',
      key: 'priority',
      align: 'center'
    },
  ];

  return (
    <div className={styles['card-box']}>
      <Card>
        <ul className={styles['card-ul']}>
            <li>
              <span className={styles['li-title']}>{item.taskCode || ''}</span>
            </li>
            {columns.map((column, index) => {
              return (
                <li key={index}>
                  <span className={styles['li-next-title']}>{column.title}</span>
                  <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span>
                </li>
              );
            })}
          
        </ul>
      </Card>
      <Sheet
        style={{ height: '500px' }}
        swipeToClose
        push
        backdrop
        opened={sheetOpened}
        onSheetClosed={() => {
          setSheetOpened(false);
        }}
      >
        <div className="swipe-handler"></div>
        <CardSheet item={sheetValue} />
      </Sheet>
    </div>
  );
};

export default CardInfo;
