import React from 'react';
import styles from './style.scss';
import { Page,PageContent, Card, CardHeader, CardContent } from '@hvisions/f-ui';

const ManualSheet = ({ item }) => {
  const columns = [
    {
      title: '托盘号',
      dataIndex: 'code',
      key: 'code',
      align: 'center'
    },
    {
      title: '任务状态',
      dataIndex: 'taskStatus',
      key: 'taskStatus',
      align: 'center'
    },
    {
      title: '托盘状态',
      dataIndex: 'state',
      key: 'state',
      align: 'center'
    },

  ];

  return (
    // <Page pageContent={false}>
      <PageContent
        infinite
        infiniteDistance={50}
      // infinitePreloader={showPreloader}
      // onInfinite={loadMore}
      // ptrPreloader={ptrPreloader}
      // ptr
      // onPtrRefresh={onHandleRefresh}
      // onPtrPullStart={() => {
      //   setPtrPreloader(true);
      // }}
      >
        <div style={{ padding: '0 16px' }} className={styles.tabContainer}>
          <ul className={styles['card-ul']}>
            <li>
              <span className={styles['li-title']}>{'托盘手动下架'}</span>
            </li>
            <Card>
              {columns.map((column, index) => {
                return (
                  <li key={index}>
                    <span className={styles['li-next-title']}>
                      {column.title}&nbsp;:&nbsp;&nbsp;&nbsp;
                    </span>
                    {/* <span className={styles['li-next-title']}>{item[column.dataIndex] || ''}</span> */}
                  </li>
                );
              })}
            </Card>
          </ul>
        </div>
      </PageContent>
  );
};

export default ManualSheet;
