const modules = {};

try {
  const actions = require.context('../store', true, /actions\.js$/);
  // console.log('------------');
  // console.log(actions.keys());
  // console.log('------------');

  actions.keys().forEach(key => {
    modules[key.replace(/(\.\/|\.js)/g, '').split('/')[0]] = actions(key);
  });

  // console.log('------------');
  // console.log(modules);
  // console.log('------------');
} catch (e) {
  // do nothing
}
export default modules;
