const modules = {};

try {
  const selectors = require.context('../store', true, /selector\.js$/);
  // console.log('------------');
  // console.log(selectors.keys());
  // console.log('------------');

  selectors.keys().forEach(key => {
    modules[key.replace(/(\.\/|\.js)/g, '').split('/')[0]] = selectors(key);
  });

  // console.log('------------');
  // console.log(modules);
  // console.log('------------');
} catch (e) {
  // do nothing
}
export default modules;
